// Normalise price, add timestamp, and fallback names
const items = $input.all();
return items.map(item => {
  const d = item.json;
  const priceNumber = parseFloat(String(d.currentPrice || '').replace(/[^0-9.]/g, ''));
  return {
    json: {
      productName: d.productName || d.name || 'Unknown',
      url: item.json.url,
      price: priceNumber || null,
      currency: d.currency || 'USD',
      availability: d.availability || 'Unknown',
      scrapedAt: new Date().toISOString()
    }
  };
});

const err = $input.item.json;
console.error('Scrape error', err);
return items;
