//Unit Tests - it checks for clarity and conciseness
const tests = [
  "Is the response clear and easy to understand?",
  "Does the response use precise and accurate information?",
];


const expanded = [];

for (const item of $input.all()) {
  for (const test of tests) {
    expanded.push({
      json: {
        provider: item.json.provider,
        response: item.json.response,
        unit_test: test
      }
    });
  }
}

return expanded;

const results = $input.all().map(i => i.json);

/** @type {Record<string, any>} */
const grouped = {};

// Group by provider and unit test
results.forEach(r => {
    const provider = r.provider || 'Unknown';
    const unitTest = r.unit_test || 'Unknown';
    const response = r.response || '';
    const score = parseFloat(r.score) || 0;

    if (!grouped[provider]) grouped[provider] = {};

    grouped[provider][unitTest] = {
        response,
        score
    };
});

// Return grouped results
return [
    {
        json: {
            groupedResults: grouped
        }
    }
];

const allItems = $input.all();

// Merge all groupedResults into a single object
/** @type {Record<string, Record<string, any>>} */
const merged = {};

allItems.forEach(item => {
    const grouped = item.json.groupedResults;
    for (const provider in grouped) {
        if (!merged[provider]) merged[provider] = {};
        const tests = grouped[provider];
        for (const testName in tests) {
            merged[provider][testName] = tests[testName];
        }
    }
});

let message = "Here are the evaluation results:\n\n";

for (const provider in merged) {
    const tests = merged[provider];
    const firstTestKey = Object.keys(tests)[0];
    const responseText = tests[firstTestKey].response || '';

    message += `Provider: ${provider}\n\n`;
    message += `Response: "${responseText}"\n\n`;
    message += `Evaluation Criteria:\n\n`;

    for (const testName in tests) {
        const score = tests[testName].score;
        message += `  "${testName}": ${score}\n\n`;
    }

    message += `\n\n`; 
}

return [
    {
        json: { message }
    }
];
