const totalCalculated= [];
for (const item of items){
  const row = item.json;
  if(row.HoursWorked === 0){
    totalCalculated.push({

      EmpId: row.EmpId,
      Name: row.Name,
      Date: row.Date,
      Hours: 0,
      Status: "Absent",
      MissingHours: 8
    });
    continue;
  }
  const start = new Date(`${row.Date} ${row.InTime}`);
const end = new Date(`${row.Date} ${row.OutTime}`);
const hours = (end.getTime() - start.getTime()) / (1000 * 60 * 60 );

const rounded = Number(hours.toFixed(2));
const missing = rounded < 8 ? Number((8 - rounded).toFixed(2)) : 0;

totalCalculated.push({
  EmpId: row.EmpId,
  Name: row.Name,
  Date: row.Date,
  Hours: rounded,
  MissingHours: missing,
  Status: rounded < 8 ? "LowHours" : "Present"

});  
}

const grouped = {};
for(const row of totalCalculated){
  const empId = row.EmpId;
  if(!grouped[empId]){
    grouped[empId] = {
      EmpId: row.EmpId,
      Name: row.Name,
      Hours: 0,
      TotalMissingHours: 0,
      TotalAbsentDays: 0,
      TotalLowHoursDays: 0,
      TotalPresentDays: 0
    };
  }
   grouped[empId].Hours += row.Hours;
  grouped[empId].TotalMissingHours += row.MissingHours;

  if (row.Status === "Absent") grouped[empId].TotalAbsentDays++;
  if (row.Status === "LowHours") grouped[empId].TotalLowHoursDays++;
  if (row.Status === "Present") grouped[empId].TotalPresentDays++;

}
return Object.values(grouped).map(emp => ({ json: emp }));

return items.map(item =>{
  const row = item.json;
  const salary = Number(row.Salary);
  const missingHours = Number(row.TotalMissingHours || 0);
  const absentDays = Number(row.TotalAbsentDays || 0);

  const hourlyRate = salary / (22 * 8);

  const deduction = (missingHours * hourlyRate) + (absentDays * 8 * hourlyRate);
  const finalSalary = salary - deduction;

  return{
    json: {
      EmpId: row.EmpId,
      Name: row.Name,
      OriginalSalary: salary,
      TotalMissingHours: missingHours,
      TotalAbsentDays: absentDays,
      Deduction: Number(deduction.toFixed(2)),
      FinalSalary: Number(finalSalary.toFixed(2)),
      EmpEmail: row.col_4
    }
  };

});

let allEmployees = [];

for (let item of items) {
    let arr = JSON.parse(item.json.output.replace(/\\n/g, " ")); 
    allEmployees.push(...arr);
}

return allEmployees.map(emp => ({ json: emp }));

