// Get configuration from Campaign Settings node
const config = $('Campaign Settings').first().json;

const items = $input.all();
const enrichedItems = [];

const startDate = new Date(config.start_date);
let cadenceDays = [];

switch(config.cadence) {
  case '1_daily':
    for(let i = 0; i < items.length; i++) cadenceDays.push(i);
    break;
  case '5_per_week':
    let dayCounter5 = 0;
    let weekDay5 = startDate.getDay();
    for(let i = 0; i < items.length; i++) {
      while(weekDay5 === 0 || weekDay5 === 6) {
        dayCounter5++;
        weekDay5 = (startDate.getDay() + dayCounter5) % 7;
      }
      cadenceDays.push(dayCounter5);
      dayCounter5++;
      weekDay5 = (startDate.getDay() + dayCounter5) % 7;
    }
    break;
  case '3_per_week':
    let dayCounter3 = 0;
    for(let i = 0; i < items.length; i++) {
      while(![1, 3, 5].includes((startDate.getDay() + dayCounter3) % 7)) dayCounter3++;
      cadenceDays.push(dayCounter3);
      dayCounter3++;
    }
    break;
}

for (let i = 0; i < items.length; i++) {
  const item = items[i];
  const scheduleDaysOffset = cadenceDays[i] ?? i;

  const scheduleDate = new Date(startDate);
  scheduleDate.setDate(scheduleDate.getDate() + scheduleDaysOffset);
  const scheduleDateStr = scheduleDate.toISOString().split('T')[0];
  const scheduleDateTime = `${scheduleDateStr}T${config.publish_hour}:00`;

  enrichedItems.push({
    json: {
      ...item.json,
      video_index: i + 1,
      total_videos: items.length,
      config: {
        drive_folder_id: config.drive_folder_id,
        profile_username: config.profile_username,
        platforms_raw: config.platforms_raw,
        timezone: config.timezone,
        start_date: config.start_date,
        cadence: config.cadence,
        publish_hour: config.publish_hour,
        sheet_id: config.sheet_id
      },
      schedule: {
        date: scheduleDateStr,
        datetime: scheduleDateTime
      }
    }
  });
}

return enrichedItems;

