const items = $input.all();
const results = [];

for (const item of items) {
  const calendarInfo = $('Build Schedule Calendar').item?.json ?? {};
  const downloadInfo = $('Download from Drive').item?.json ?? {};

  const makeBaseRow = () => ({
    "Video ID": calendarInfo?.id || downloadInfo?.id || 'unknown',
    "Video Name": downloadInfo?.name || calendarInfo?.name || 'unknown',
    "Index": String(calendarInfo?.video_index ?? ''),
    "Status": 'draft',
    "Schedule Date": calendarInfo?.schedule?.date || '',
    "Schedule DateTime": calendarInfo?.schedule?.datetime || '',

    "TikTok Title": '',
    "TikTok Description": '',
    "TikTok Hashtags": '',

    "Instagram Title": '',
    "Instagram Description": '',
    "Instagram Hashtags": '',

    "YouTube Title": '',
    "YouTube Description": '',
    "YouTube Tags": '',

    "Summary": '',
    "Profile": calendarInfo?.config?.profile_username || '',
    "Platforms": calendarInfo?.config?.platforms_raw || '',
    "Created At": new Date().toISOString()
  });

  try {
    const rawOutput = item.json.output ?? item.json.text ?? item.json;

    let content;
    if (rawOutput && typeof rawOutput === 'object' && !Array.isArray(rawOutput)) {
      content = rawOutput;
    } else {
      let cleanedOutput = String(rawOutput)
        .replace(/```json\n?/g, '')
        .replace(/```\n?/g, '')
        .trim();

      const firstBrace = cleanedOutput.indexOf('{');
      const lastBrace = cleanedOutput.lastIndexOf('}');
      if (firstBrace !== -1 && lastBrace !== -1 && lastBrace > firstBrace) {
        cleanedOutput = cleanedOutput.slice(firstBrace, lastBrace + 1);
      }

      content = JSON.parse(cleanedOutput);
    }

    const row = makeBaseRow();

    row["TikTok Title"] = content.tiktok?.title || '';
    row["TikTok Description"] = content.tiktok?.description || '';
    row["TikTok Hashtags"] = content.tiktok?.hashtags || '';

    row["Instagram Title"] = content.instagram?.title || '';
    row["Instagram Description"] = content.instagram?.description || '';
    row["Instagram Hashtags"] = content.instagram?.hashtags || '';

    row["YouTube Title"] = content.youtube?.title || '';
    row["YouTube Description"] = content.youtube?.description || '';
    row["YouTube Tags"] = content.youtube?.tags || '';

    row["Summary"] = content.summary || '';

    results.push({ json: row });
  } catch (error) {
    const row = makeBaseRow();
    row["Status"] = 'error';
    row["Summary"] = `PARSE_ERROR: ${error.message}`;
    results.push({ json: row });
  }
}

return results;

const item = $input.first();
const sheetData = item.json;
const config = $('Publisher Config').first().json;

const platforms = (sheetData.Platforms || 'tiktok,instagram,youtube').toLowerCase();

return {
  json: {
    video_id: sheetData['Video ID'],
    video_name: sheetData['Video Name'],
    row_index: sheetData.row_number,

    has_tiktok: platforms.includes('tiktok'),
    has_instagram: platforms.includes('instagram'),
    has_youtube: platforms.includes('youtube'),

    tiktok_content: [sheetData['TikTok Title'], sheetData['TikTok Description'], sheetData['TikTok Hashtags']].filter(Boolean).join('\n\n'),
    instagram_content: [sheetData['Instagram Title'], sheetData['Instagram Description'], sheetData['Instagram Hashtags']].filter(Boolean).join('\n\n'),

    youtube_title: sheetData['YouTube Title'] || '',
    youtube_description: sheetData['YouTube Description'] || '',

    schedule_datetime: sheetData['Schedule DateTime'] || '',

    profile_username: sheetData.Profile || config.profile_username,
    sheet_id: config.sheet_id
