const today = new Date();

return items.filter(item => {
  const data = item.json;

  const dischargeDate = new Date(data.start_date);
  const followUpDays = Number(data["Follow_up_duration(days)"]);

  // Calculate how many days since discharge
  const daysSinceDischarge = Math.floor((today - dischargeDate) / (1000 * 60 * 60 * 24));

  // Conditions:
  // - Patient already discharged (no future dates, no today)
  // - Still within follow-up window
  return daysSinceDischarge > 0 && daysSinceDischarge <= followUpDays;
});

// This function expects input like:
// status: read\nintensity: moderate

const output = $json["output"]; // The raw message from AI, e.g., status: read\nintensity: moderate
const lines = output.split('\n');

let result = {};

for (const line of lines) {
  const [key, value] = line.split(':').map(s => s.trim());
  if (key && value) {
    result[key] = value;
  }
}

return [{ json: result }];

