// --- Modern n8n Code node version (no `items` implicit var) ---
const inputItems = $input.all(); // get all merged inputs

// Find record containing lastCheck
const lastCheckItem = inputItems.find(i => i.json.lastCheck);
const lastCheckStr = lastCheckItem ? lastCheckItem.json.lastCheck : null;
if (!lastCheckStr) {
  return [{ json: { newDeals: [] } }];
}

// Convert to UTC (10-minute safety buffer)
const lastCheckUTC = Date.parse(lastCheckStr) - 10 * 60 * 1000;

// Collect all deals from input
const deals = inputItems
  .map(i => i.json)
  .filter(i => i.Created_Time);

// Filter only new ones
const newDeals = deals.filter(d => {
  const createdUTC = Date.parse(d.Created_Time);
  return !isNaN(createdUTC) && createdUTC > lastCheckUTC;
});

// Return result in new n8n format
return [{ json: { newDeals } }];

// Extract competitor name and first URL from Description
const desc = $json.newDeals.Description || '';

const urlMatch = desc.match(/https?:\/\/[^\s]+/);
const nameMatch = desc.match(/^([\w\s\-\&]+)/);

return {
  json: {
    ...$json,
    competitorName: nameMatch ? nameMatch[1].trim() : '',
    competitorUrl: urlMatch ? urlMatch[0] : ''
  }
};

