// Debug: Check what binary data we have
const inputData = $input.first();
const binaryKeys = Object.keys(inputData.binary || {});

// Get the first binary key (might not be 'data')
const binaryKey = binaryKeys[0] || 'data';
const binaryData = inputData.binary[binaryKey];

const base64 = binaryData.data;

// Detect mime type from the original message
const telegramMessage = $('Telegram Webhook').first().json.body.message;
let mimeType = 'image/jpeg';

if (telegramMessage.document) {
  // It's a document (PDF)
  mimeType = telegramMessage.document.mime_type || 'application/pdf';
} else if (telegramMessage.photo) {
  // It's a photo
  mimeType = 'image/jpeg';
}

const dataUri = `data:${mimeType};base64,${base64}`;

// Get chat ID from original webhook
const chatId = $('Telegram Webhook').first().json.body.message.chat.id;

return {
  json: {
    dataUri: dataUri,
    chatId: chatId,
    mimeType: mimeType,
    debug_binaryKeys: binaryKeys,
    debug_base64_length: base64 ? base64.length : 0
  }
};

const extractionResult = $input.first().json;
const chatId = $('Convert to Base64').first().json.chatId;
const telegramMessage = $('Telegram Webhook').first().json.body.message;
const caption = telegramMessage.caption || '';

let receiptData;
if (extractionResult.data && extractionResult.data.receipt_data) {
  receiptData = extractionResult.data.receipt_data;
} else if (extractionResult.receipt_data) {
  receiptData = extractionResult.receipt_data;
} else {
  receiptData = extractionResult.data || extractionResult;
}

let category = receiptData.category || 'Other';
const vendorName = receiptData.vendor_name || 'Unknown';
const totalAmount = parseFloat(receiptData.total_amount) || 0;
const currency = receiptData.currency || 'EUR';
const transactionDate = receiptData.transaction_date || new Date().toLocaleDateString('de-DE');
const confidence = receiptData.extraction_confidence || 'medium';

const validCategories = [
  'Restaurant',
  'Transportation', 
  'Mobile Phone',
  'Office Supplies',
  'Accommodation',
  'Marketing',
  'Software',
  'Equipment'
];

let captionCategory = '';
let captionDetails = '';

if (caption.trim()) {
  const captionTrimmed = caption.trim();

  if (captionTrimmed.includes(' - ')) {
    const parts = captionTrimmed.split(' - ');
    const possibleCategory = parts[0].trim();
    const possibleDetails = parts.slice(1).join(' - ').trim();

    for (const validCat of validCategories) {
      if (possibleCategory.toLowerCase() === validCat.toLowerCase()) {
        captionCategory = validCat;
        captionDetails = possibleDetails;
        break;
      }
    }

    if (!captionCategory) {
      captionDetails = captionTrimmed;
    }
  } else {
    for (const validCat of validCategories) {
      if (captionTrimmed.toLowerCase() === validCat.toLowerCase()) {
        captionCategory = validCat;
        break;
      }
    }

    if (!captionCategory) {
      captionDetails = captionTrimmed;
    }
  }
}

if (captionCategory) {
  category = captionCategory;
}

let eurRate = 1;
if (category === 'Mobile Phone') {
  eurRate = 0.8;
}
const eurAmount = totalAmount * eurRate;

let monthName = 'December';
let year = '2025';
try {
  const dateParts = transactionDate.split('.');
  if (dateParts.length === 3) {
    const monthNum = parseInt(dateParts[1]);
    const monthNames = ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'];
    monthName = monthNames[monthNum - 1] || 'December';
    year = dateParts[2];
  }
} catch (e) {}

const sheetName = monthName + '_' + year;

let details = '';
if (captionDetails) {
  details = captionDetails;
} else if (captionCategory) {
  details = 'Business Expense';
} else if (category === 'Restaurant') {
  details = 'Business Meal';
} else if (category === 'Transportation') {
  details = 'Business Travel';
} else if (category === 'Mobile Phone') {
  details = 'Phone Bill';
} else if (category !== 'Other') {
  details = 'Business Expense';
} else {
  details = 'Other';
}

const needsCategoryInput = (category === 'Other');

return {
  json: {
    category: category,
    vendor_name: vendorName,
    total_amount: totalAmount,
    currency: currency,
    transaction_date: transactionDate,
    confidence: confidence,
    eur_rate: eurRate,
    eur_amount: eurAmount,
    month_name: monthName,
    year: year,
    sheet_name: sheetName,
    details: details,
    chat_id: chatId,
    needs_category_input: needsCategoryInput
  }
};

