const monthName = $('Process Extraction').item.json.month_name;
const year = $('Process Extraction').item.json.year;

// Get the sheetId from the Create Sheet response
const createSheetResponse = $('Create Sheet').item.json;
const sheetId = createSheetResponse.replies?.[0]?.addSheet?.properties?.sheetId || 0;

return {
  json: {
    requestBody: {
      requests: [
        // Row 1: Expense Report header (orange background, white text)
        {
          updateCells: {
            rows: [
              {
                values: [
                  { userEnteredValue: { stringValue: "Expense Report" }, userEnteredFormat: { textFormat: { bold: true, fontSize: 14, foregroundColor: { red: 1, green: 1, blue: 1 } }, backgroundColor: { red: 0.82, green: 0.29, blue: 0.15 } } },
                  { userEnteredValue: { stringValue: "" }, userEnteredFormat: { backgroundColor: { red: 0.82, green: 0.29, blue: 0.15 } } },
                  { userEnteredValue: { stringValue: "" }, userEnteredFormat: { backgroundColor: { red: 0.82, green: 0.29, blue: 0.15 } } },
                  { userEnteredValue: { stringValue: "Month:" }, userEnteredFormat: { textFormat: { bold: true, foregroundColor: { red: 1, green: 1, blue: 1 } }, backgroundColor: { red: 0.82, green: 0.29, blue: 0.15 } } },
                  { userEnteredValue: { stringValue: monthName }, userEnteredFormat: { textFormat: { bold: true, foregroundColor: { red: 1, green: 1, blue: 1 } }, backgroundColor: { red: 0.82, green: 0.29, blue: 0.15 } } },
                  { userEnteredValue: { stringValue: "Year:" }, userEnteredFormat: { textFormat: { bold: true, foregroundColor: { red: 1, green: 1, blue: 1 } }, backgroundColor: { red: 0.82, green: 0.29, blue: 0.15 } } },
                  { userEnteredValue: { numberValue: parseInt(year) }, userEnteredFormat: { textFormat: { bold: true, foregroundColor: { red: 1, green: 1, blue: 1 } }, backgroundColor: { red: 0.82, green: 0.29, blue: 0.15 } } }
                ]
              },
              // Row 2: Empty row (white background)
              {
                values: [
                  { userEnteredValue: { stringValue: "" }, userEnteredFormat: { backgroundColor: { red: 1, green: 1, blue: 1 } } },
                  { userEnteredValue: { stringValue: "" }, userEnteredFormat: { backgroundColor: { red: 1, green: 1, blue: 1 } } },
                  { userEnteredValue: { stringValue: "" }, userEnteredFormat: { backgroundColor: { red: 1, green: 1, blue: 1 } } },
                  { userEnteredValue: { stringValue: "" }, userEnteredFormat: { backgroundColor: { red: 1, green: 1, blue: 1 } } },
                  { userEnteredValue: { stringValue: "" }, userEnteredFormat: { backgroundColor: { red: 1, green: 1, blue: 1 } } },
                  { userEnteredValue: { stringValue: "" }, userEnteredFormat: { backgroundColor: { red: 1, green: 1, blue: 1 } } },
                  { userEnteredValue: { stringValue: "" }, userEnteredFormat: { backgroundColor: { red: 1, green: 1, blue: 1 } } }
                ]
              },
              // Row 3: Column headers (orange background, white text)
              {
                values: [
                  { userEnteredValue: { stringValue: "Date" }, userEnteredFormat: { textFormat: { bold: true, foregroundColor: { red: 1, green: 1, blue: 1 } }, backgroundColor: { red: 0.82, green: 0.29, blue: 0.15 }, horizontalAlignment: "CENTER" } },
                  { userEnteredValue: { stringValue: "Category" }, userEnteredFormat: { textFormat: { bold: true, foregroundColor: { red: 1, green: 1, blue: 1 } }, backgroundColor: { red: 0.82, green: 0.29, blue: 0.15 }, horizontalAlignment: "CENTER" } },
                  { userEnteredValue: { stringValue: "Vendor" }, userEnteredFormat: { textFormat: { bold: true, foregroundColor: { red: 1, green: 1, blue: 1 } }, backgroundColor: { red: 0.82, green: 0.29, blue: 0.15 }, horizontalAlignment: "CENTER" } },
                  { userEnteredValue: { stringValue: "Details" }, userEnteredFormat: { textFormat: { bold: true, foregroundColor: { red: 1, green: 1, blue: 1 } }, backgroundColor: { red: 0.82, green: 0.29, blue: 0.15 }, horizontalAlignment: "CENTER" } },
                  { userEnteredValue: { stringValue: "Amount" }, userEnteredFormat: { textFormat: { bold: true, foregroundColor: { red: 1, green: 1, blue: 1 } }, backgroundColor: { red: 0.82, green: 0.29, blue: 0.15 }, horizontalAlignment: "CENTER" } },
                  { userEnteredValue: { stringValue: "Currency" }, userEnteredFormat: { textFormat: { bold: true, foregroundColor: { red: 1, green: 1, blue: 1 } }, backgroundColor: { red: 0.82, green: 0.29, blue: 0.15 }, horizontalAlignment: "CENTER" } },
                  { userEnteredValue: { stringValue: "EUR Amount" }, userEnteredFormat: { textFormat: { bold: true, foregroundColor: { red: 1, green: 1, blue: 1 } }, backgroundColor: { red: 0.82, green: 0.29, blue: 0.15 }, horizontalAlignment: "CENTER" } }
                ]
              }
            ],
            fields: "userEnteredValue,userEnteredFormat",
            start: { sheetId: sheetId, rowIndex: 0, columnIndex: 0 }
          }
        },
        // Pre-format data rows 4-49 with white background and number format for columns E and G
        {
          repeatCell: {
            range: {
              sheetId: sheetId,
              startRowIndex: 3,
              endRowIndex: 49,
              startColumnIndex: 0,
              endColumnIndex: 7
            },
            cell: {
              userEnteredFormat: {
                backgroundColor: { red: 1, green: 1, blue: 1 },
                wrapStrategy: "WRAP"
              }
            },
            fields: "userEnteredFormat.backgroundColor,userEnteredFormat.wrapStrategy"
          }
        },
        // Set number format for Amount column (E)
        {
          repeatCell: {
            range: {
              sheetId: sheetId,
              startRowIndex: 3,
              endRowIndex: 49,
              startColumnIndex: 4,
              endColumnIndex: 5
            },
            cell: {
              userEnteredFormat: {
                numberFormat: { type: "NUMBER", pattern: "#,##0.00" }
              }
            },
            fields: "userEnteredFormat.numberFormat"
          }
        },
        // Set number format for EUR Amount column (G)
        {
          repeatCell: {
            range: {
              sheetId: sheetId,
              startRowIndex: 3,
              endRowIndex: 49,
              startColumnIndex: 6,
              endColumnIndex: 7
            },
            cell: {
              userEnteredFormat: {
                numberFormat: { type: "NUMBER", pattern: "#,##0.00" }
              }
            },
            fields: "userEnteredFormat.numberFormat"
          }
        },
        // Row 50: TOTAL row (orange background, white text, with SUM formula)
        {
          updateCells: {
            rows: [
              {
                values: [
                  { userEnteredValue: { stringValue: "" }, userEnteredFormat: { backgroundColor: { red: 0.82, green: 0.29, blue: 0.15 } } },
                  { userEnteredValue: { stringValue: "" }, userEnteredFormat: { backgroundColor: { red: 0.82, green: 0.29, blue: 0.15 } } },
                  { userEnteredValue: { stringValue: "" }, userEnteredFormat: { backgroundColor: { red: 0.82, green: 0.29, blue: 0.15 } } },
                  { userEnteredValue: { stringValue: "" }, userEnteredFormat: { backgroundColor: { red: 0.82, green: 0.29, blue: 0.15 } } },
                  { userEnteredValue: { stringValue: "" }, userEnteredFormat: { backgroundColor: { red: 0.82, green: 0.29, blue: 0.15 } } },
                  { userEnteredValue: { stringValue: "TOTAL:" }, userEnteredFormat: { textFormat: { bold: true, foregroundColor: { red: 1, green: 1, blue: 1 } }, backgroundColor: { red: 0.82, green: 0.29, blue: 0.15 }, horizontalAlignment: "RIGHT" } },
                  { userEnteredValue: { formulaValue: "=SUM(G4:G49)" }, userEnteredFormat: { textFormat: { bold: true, foregroundColor: { red: 1, green: 1, blue: 1 } }, backgroundColor: { red: 0.82, green: 0.29, blue: 0.15 }, numberFormat: { type: "CURRENCY", pattern: "‚Ç¨#,##0.00" } } }
                ]
              }
            ],
            fields: "userEnteredValue,userEnteredFormat",
            start: { sheetId: sheetId, rowIndex: 49, columnIndex: 0 }
          }
        },
        // Set column widths
        {
          updateDimensionProperties: {
            range: { sheetId: sheetId, dimension: "COLUMNS", startIndex: 0, endIndex: 7 },
            properties: { pixelSize: 120 },
            fields: "pixelSize"
          }
        },
        // Make Details column wider
        {
          updateDimensionProperties: {
            range: { sheetId: sheetId, dimension: "COLUMNS", startIndex: 3, endIndex: 4 },
            properties: { pixelSize: 180 },
            fields: "pixelSize"
          }
        }
      ]
    },
    sheetId: sheetId
  }
};

const data = $('Process Extraction').item.json;

// Parse amounts as numbers
let totalAmount = Number(data.total_amount);
let eurAmount = Number(data.eur_amount);

if (isNaN(totalAmount)) {
  totalAmount = Number(String(data.total_amount).replace(/[^0-9.-]/g, '')) || 0;
}
if (isNaN(eurAmount)) {
  eurAmount = Number(String(data.eur_amount).replace(/[^0-9.-]/g, '')) || 0;
}

// Get sheetId from either Build Headers Request (new sheet) or Extract Sheet ID (existing sheet)
let sheetId = 0;

try {
  sheetId = $('Build Headers Request').item.json.sheetId;
} catch (e) {
  // New sheet path did not run
}

if (!sheetId && sheetId !== 0) {
  try {
    sheetId = $('Extract Sheet ID').item.json.sheetId;
  } catch (e) {
    // Existing sheet path did not run
  }
}

// If still no sheetId, try Extract Sheet ID again (it might be 0 which is falsy)
if (sheetId === 0) {
  try {
    const extractedId = $('Extract Sheet ID').item.json.sheetId;
    if (extractedId !== undefined) {
      sheetId = extractedId;
    }
  } catch (e) {
    // Keep default
  }
}

return {
  json: {
    expenseBody: {
      values: [[
        data.transaction_date,
        data.category,
        data.vendor_name,
        data.details || "",
        totalAmount,
        data.currency,
        eurAmount
      ]]
    },
    sheetName: data.sheet_name,
    sheetId: sheetId,
    eurAmount: eurAmount,
    totalAmount: totalAmount
  }
};

// Get the row number from the Add Expense Row response
const response = $('Add Expense Row').item.json;
const updatedRange = response.updates?.updatedRange || response.updatedRange || "";

// Extract row number from range like "December_2025!A4:G4"
const rowMatch = updatedRange.match(/!A(\d+)/);
const rowNumber = rowMatch ? parseInt(rowMatch[1]) : 4;

// Get sheetId from Build Expense Data
const sheetId = $('Build Expense Data').item.json.sheetId;

return {
  json: {
    formatBody: {
      requests: [
        {
          repeatCell: {
            range: {
              sheetId: sheetId,
              startRowIndex: rowNumber - 1,
              endRowIndex: rowNumber,
              startColumnIndex: 0,
              endColumnIndex: 7
            },
            cell: {
              userEnteredFormat: {
                backgroundColor: { red: 1, green: 1, blue: 1 },
                textFormat: { 
                  foregroundColor: { red: 0, green: 0, blue: 0 },
                  bold: true
                },
                wrapStrategy: "WRAP"
              }
            },
            fields: "userEnteredFormat.backgroundColor,userEnteredFormat.textFormat,userEnteredFormat.wrapStrategy"
          }
        }
      ]
    },
    rowNumber: rowNumber
  }
};

const sheetsData = $('Get Sheet Info').item.json;
const sheetName = $('Process Extraction').item.json.sheet_name;

let sheetId = 0;

if (sheetsData.sheets) {
  for (const sheet of sheetsData.sheets) {
    if (sheet.properties.title === sheetName) {
      sheetId = sheet.properties.sheetId;
      break;
    }
  }
}

return {
  json: {
    sheetId: sheetId
  }
};
