const payload = $input.first().json || {};
const data = payload.data || payload;

const cleanText = (value, fallback = '') => {
  if (Array.isArray(value)) return fallback;
  if (typeof value === 'number') return value.toString();
  return value || fallback;
};

const buildGallery = () => {
  if (!Array.isArray(data.images) || data.images.length === 0) {
    return '<div class="gallery-item"><div class="placeholder-image">No photos available</div></div>';
  }
  return data.images.slice(0, 6).map((url, index) => '<div class="gallery-item"><img src="' + url + '" alt="Property photo ' + (index + 1) + '" loading="lazy"></div>').join('');
};

const formatAmenity = (amenity) => {
  if (typeof amenity === 'string') return amenity;
  if (amenity && (amenity.name || amenity.title)) return amenity.name || amenity.title;
  return '';
};

const buildAmenities = () => {
  if (!Array.isArray(data.amenities) || data.amenities.length === 0) {
    return '<li><span class="amenity-icon">‚úì</span> Basic amenities</li>';
  }
  return data.amenities.slice(0, 12).map((amenity) => formatAmenity(amenity)).filter(Boolean).map((label) => '<li><span class="amenity-icon">‚úì</span> ' + label + '</li>').join('');
};

const buildHouseRules = () => {
  const rules = Array.isArray(data.houseRules) ? data.houseRules.slice(0, 6).map((rule) => {
    if (typeof rule === 'string') return rule;
    if (rule && (rule.title || rule.name)) return rule.title || rule.name;
    return '';
  }).filter(Boolean) : [];
  if (!rules.length) {
    return '<li>Check-in: 3:00 PM</li><li>Checkout: 11:00 AM</li><li>No smoking</li>';
  }
  return rules.map((rule) => '<li>' + rule + '</li>').join('');
};

const galleryHtml = buildGallery();
const amenitiesHtml = buildAmenities();
const houseRulesHtml = buildHouseRules();

const locationParts = [data.location?.city, data.location?.state, data.location?.country].filter(Boolean).join(', ');
const price = data.pricing?.pricePerNight || data.price || data.pricing?.basePrice || 100;
const currency = data.pricing?.currency || '$';
const title = cleanText(data.title, 'Beautiful Vacation Rental');
const description = cleanText(data.description, 'A wonderful place to stay');

const html = '<!DOCTYPE html><html lang="en"><head><meta charset="UTF-8"><meta name="viewport" content="width=device-width, initial-scale=1.0"><title>' + title + ' | Direct Booking</title><meta name="description" content="' + description.substring(0, 160) + '"><style>* { margin: 0; padding: 0; box-sizing: border-box; } :root { --primary: #FF5A5F; --primary-dark: #E04850; --text: #222222; --text-light: #717171; --border: #DDDDDD; --background: #FFFFFF; --background-light: #F7F7F7; } body { font-family: -apple-system, BlinkMacSystemFont, Segoe UI, Roboto, sans-serif; color: var(--text); line-height: 1.6; background: var(--background); } .container { max-width: 1120px; margin: 0 auto; padding: 0 24px; } header { border-bottom: 1px solid var(--border); padding: 20px 0; position: sticky; top: 0; background: var(--background); z-index: 100; } .header-content { display: flex; justify-content: space-between; align-items: center; } .logo { font-size: 24px; font-weight: 700; color: var(--primary); text-decoration: none; } .book-now-btn { background: var(--primary); color: white; padding: 12px 24px; border-radius: 8px; text-decoration: none; font-weight: 600; transition: background 0.2s; } .book-now-btn:hover { background: var(--primary-dark); } .gallery { display: grid; grid-template-columns: repeat(4, 1fr); gap: 8px; margin: 24px 0; border-radius: 12px; overflow: hidden; } .gallery-item:first-child { grid-column: span 2; grid-row: span 2; } .gallery-item img { width: 100%; height: 100%; object-fit: cover; min-height: 200px; } .gallery-item:first-child img { min-height: 416px; } .placeholder-image { background: linear-gradient(135deg, #667eea 0%, #764ba2 100%); color: white; display: flex; align-items: center; justify-content: center; min-height: 200px; font-size: 18px; } .main-content { display: grid; grid-template-columns: 1fr 380px; gap: 48px; padding: 32px 0; } .listing-title { font-size: 26px; font-weight: 600; margin-bottom: 8px; } .listing-location { color: var(--text-light); margin-bottom: 16px; } .listing-stats { display: flex; gap: 16px; padding: 16px 0; border-bottom: 1px solid var(--border); flex-wrap: wrap; } .stat { display: flex; align-items: center; gap: 6px; } .rating { display: flex; align-items: center; gap: 4px; } .star { color: var(--primary); } .host-section { display: flex; align-items: center; gap: 16px; padding: 24px 0; border-bottom: 1px solid var(--border); } .host-photo { width: 56px; height: 56px; border-radius: 50%; object-fit: cover; background: var(--border); } .host-info h3 { font-size: 16px; font-weight: 600; } .superhost-badge { display: inline-flex; align-items: center; gap: 4px; font-size: 12px; color: var(--text-light); margin-top: 4px; } .section { padding: 32px 0; border-bottom: 1px solid var(--border); } .section-title { font-size: 22px; font-weight: 600; margin-bottom: 16px; } .description { color: var(--text); white-space: pre-line; } .amenities-grid { display: grid; grid-template-columns: repeat(2, 1fr); gap: 16px; } .amenities-grid li { list-style: none; display: flex; align-items: center; gap: 12px; } .amenity-icon { color: var(--primary); font-weight: bold; } .rules-list { list-style: none; } .rules-list li { padding: 8px 0; border-bottom: 1px solid var(--border); } .rules-list li:last-child { border-bottom: none; } .booking-card { position: sticky; top: 100px; background: var(--background); border: 1px solid var(--border); border-radius: 12px; padding: 24px; box-shadow: 0 6px 16px rgba(0,0,0,0.12); } .price-line { display: flex; align-items: baseline; gap: 4px; margin-bottom: 24px; } .price { font-size: 22px; font-weight: 600; } .price-period { color: var(--text-light); } .booking-form { border: 1px solid var(--border); border-radius: 8px; margin-bottom: 16px; } .form-row { display: grid; grid-template-columns: 1fr 1fr; } .form-field { padding: 12px; border-bottom: 1px solid var(--border); } .form-field:nth-child(odd) { border-right: 1px solid var(--border); } .form-field.full-width { grid-column: span 2; border-bottom: none; } .form-field label { display: block; font-size: 10px; font-weight: 600; text-transform: uppercase; margin-bottom: 4px; } .form-field input, .form-field select { width: 100%; border: none; font-size: 14px; background: transparent; outline: none; } .reserve-btn { width: 100%; background: linear-gradient(to right, var(--primary), var(--primary-dark)); color: white; border: none; padding: 14px; border-radius: 8px; font-size: 16px; font-weight: 600; cursor: pointer; transition: transform 0.2s, box-shadow 0.2s; } .reserve-btn:hover { transform: translateY(-1px); box-shadow: 0 4px 12px rgba(255, 90, 95, 0.4); } .booking-note { text-align: center; color: var(--text-light); font-size: 14px; margin-top: 16px; } footer { background: var(--background-light); padding: 48px 0; margin-top: 48px; } .footer-content { text-align: center; color: var(--text-light); } @media (max-width: 768px) { .main-content { grid-template-columns: 1fr; } .gallery { grid-template-columns: 1fr; } .gallery-item:first-child { grid-column: span 1; grid-row: span 1; } .booking-card { position: relative; top: 0; } .amenities-grid { grid-template-columns: 1fr; } }</style></head><body><header><div class="container header-content"><a href="#" class="logo">StayDirect</a><a href="#booking" class="book-now-btn">Book Now</a></div></header><main class="container"><div class="gallery">' + galleryHtml + '</div><div class="main-content"><div class="listing-details"><h1 class="listing-title">' + title + '</h1><p class="listing-location">' + locationParts + '</p><div class="listing-stats"><span class="stat">üè† ' + (data.maxGuests || 4) + ' guests</span><span class="stat">üõèÔ∏è ' + (data.bedrooms || 1) + ' bedroom' + ((data.bedrooms || 1) > 1 ? 's' : '') + '</span><span class="stat">üõÅ ' + (data.bathrooms || 1) + ' bath' + ((data.bathrooms || 1) > 1 ? 's' : '') + '</span><span class="stat">üõèÔ∏è ' + (data.beds || 1) + ' bed' + ((data.beds || 1) > 1 ? 's' : '') + '</span><span class="rating"><span class="star">‚òÖ</span><strong>' + (data.reviewScore?.overallRating || 4.8) + '</strong><span>(' + (data.reviewScore?.reviewsCount || 0) + ' reviews)</span></span></div><div class="host-section">' + (data.hostProfile?.photo ? '<img src="' + data.hostProfile.photo + '" alt="' + (data.hostProfile?.name || 'Your Host') + '" class="host-photo">' : '<div class="host-photo"></div>') + '<div class="host-info"><h3>Hosted by ' + (data.hostProfile?.name || 'Your Host') + '</h3>' + (data.hostProfile?.isSuperhost ? '<span class="superhost-badge">üèÜ Superhost</span>' : '') + '</div></div><div class="section"><h2 class="section-title">About this place</h2><p class="description">' + description + '</p></div><div class="section"><h2 class="section-title">What this place offers</h2><ul class="amenities-grid">' + amenitiesHtml + '</ul></div><div class="section"><h2 class="section-title">House Rules</h2><ul class="rules-list">' + houseRulesHtml + '</ul></div></div><div class="booking-sidebar"><div class="booking-card" id="booking"><div class="price-line"><span class="price">' + currency + price + '</span><span class="price-period">/ night</span></div><form class="booking-form"><div class="form-row"><div class="form-field"><label>Check-in</label><input type="date" name="checkin" required></div><div class="form-field"><label>Checkout</label><input type="date" name="checkout" required></div></div><div class="form-row"><div class="form-field full-width"><label>Guests</label><select name="guests">' + Array.from({length: data.maxGuests || 4}, (_, i) => '<option value="' + (i+1) + '">' + (i+1) + ' guest' + (i > 0 ? 's' : '') + '</option>').join('') + '</select></div></div></form><button type="submit" class="reserve-btn">Reserve</button><p class="booking-note">You won\'t be charged yet</p></div></div></div></main><footer><div class="container footer-content"><p>¬© 2025 ' + title + '. All rights reserved.</p><p style="margin-top: 8px;">Book directly and save on fees!</p><p style="margin-top: 16px;">Powered by <a href="https://sitebuilder.shortrentals.ai/" target="_blank" style="color: var(--primary); text-decoration: none;">shortrentals.ai</a></p></div></footer></body></html>';

const slug = title.toLowerCase().replace(/[^a-z0-9]+/g, '-').replace(/^-|-$/g, '').substring(0, 30) || 'direct-booking';
const siteName = 'booking-' + slug + '-' + Date.now().toString(36);

return { html, siteName, title, description: description.substring(0, 160) };

const html = $input.first().json.html;
const siteName = $input.first().json.siteName;
const title = $input.first().json.title;

const binaryData = await this.helpers.prepareBinaryData(
  Buffer.from(html, 'utf-8'),
  'index.html',
  'text/html'
);

return {
  json: { siteName, title, html },
  binary: { data: binaryData }
};

const siteData = $('Create Netlify Site').item.json;
const deployData = $input.first().json;
const prepData = $('Prepare Binary').item.json;

return {
  success: true,
  message: 'Direct booking site deployed successfully!',
  title: prepData.title,
  siteUrl: siteData.ssl_url || siteData.url,
  adminUrl: siteData.admin_url,
  siteId: siteData.site_id,
  deployId: deployData.id,
  deployState: deployData.state,
  note: 'Your site is now live! Visit the siteUrl to see your direct booking page.'
};
