const supplierOrders = {};
const orderDate = new Date().toISOString().split("T")[0];
const poNumber = `PO-${Date.now()}`;

// Group by supplier
for (const item of items) {
  const { sku, name, supplier, supplier_email, reorder_needed, order_qty, available, threshold } = item.json;

  if (reorder_needed) {
    if (!supplierOrders[supplier_email]) {
      supplierOrders[supplier_email] = {
        supplier,
        supplierEmail: supplier_email,
        items: []
      };
    }

    supplierOrders[supplier_email].items.push({
      sku,
      name,
      orderQty: order_qty,
      available,
      threshold
    });
  }
}

const results = [];

for (const supplierEmail in supplierOrders) {
  const order = supplierOrders[supplierEmail];
  const itemList = order.items.map(i =>
    `- ${i.name} (SKU: ${i.sku})\n   Order Qty: ${i.orderQty}`
  ).join("\n");

  // Email
  const emailSubject = `Purchase Order ${poNumber} - ${order.supplier}`;
  const emailBody = `Dear ${order.supplier},\n\n` +
    `We would like to place the following order:\n\n${itemList}\n\n` +
    `PO Number: ${poNumber}\nOrder Date: ${orderDate}\n\n` +
    `Please confirm availability and expected delivery.\n\n` +
    `Best regards,\nProcurement Team`;

  results.push({
    json: {
      type: "email",
      supplier: order.supplier,
      supplier_email: order.supplierEmail,
      subject: emailSubject,
      body: emailBody
    }
  });

  // PO Rows (for Sheet)
  for (const i of order.items) {
    results.push({
      json: {
        type: "po",
        po_number: poNumber,
        supplier: order.supplier,
        supplier_email: order.supplierEmail,
        sku: i.sku,
        item_name: i.name,
        available: i.available,
        threshold: i.threshold,
        order_qty: i.orderQty,
        order_date: orderDate
      }
    });
  }
}

return results;

const results = [];

for (const item of items) {
  const sku = item.json.sku;
  const name = item.json.Name;
  const supplier = item.json.supplier;
  const supplierEmail = item.json.supplier_email;

  const available = Number(item.json["Available Qty"]) || 0;
  const threshold = Number(item.json["Min. Threshold"]) || 0;
  const minOrder = Number(item.json["Min Order"]) || 0;
  const maxOrder = Number(item.json["Max Order"]) || 0;

  let reorderNeeded = false;
  let orderQty = 0;

  // Check if reorder needed
  if (available < threshold) {
    reorderNeeded = true;

    // Basic calculation: how much short
    orderQty = threshold - available;

    // Ensure reorderQty >= Min Order
    if (orderQty < minOrder) {
      orderQty = minOrder;
    }

    // Ensure reorderQty <= Max Order
    if (orderQty > maxOrder) {
      orderQty = maxOrder;
    }
  }

  results.push({
    json: {
      sku,
      name,
      supplier,
      supplier_email: supplierEmail,
      available,
      threshold,
      minOrder,
      maxOrder,
      reorder_needed: reorderNeeded,
      order_qty: orderQty
    }
  });
}

return results;
