const deal = items[0].json;

return [
  {
    json: {
      dealId: deal.id,
      dealName: deal.Deal_Name,
      stage: deal.Stage,
      amount: deal.Amount,
      description: deal.Description,
      leadSource: deal.Lead_Source,
      contactName: deal.Contact_Name?.name,
      accountName: deal.Account_Name?.name,
      // you can add more fields here if needed
    }
  }
];

// Create an empty object
const combinedItem = {};

// Merge all incoming items (only real JSON objects)
for (const item of items) {
    if (item && item.json) {
        Object.assign(combinedItem, item.json);
    }
}

// ---- SAFE DEAL CONTEXT ----
let dealData = null;
try {
    const dealNode = $("Extract Deal Context");
    dealData = dealNode?.item?.json ?? null;
} catch (e) {
    dealData = null;
}
combinedItem.dealContext = dealData ? [dealData] : [];

// ---- SAFE LEAD CONTEXT ----
let leadData = null;
try {
    const leadNode = $("Lead Context");
    leadData = leadNode?.item?.json ?? null;
} catch (e) {
    leadData = null;
}

// return merged output
return [{ json: combinedItem }];

// Get AI text output (the part inside ```json ... ```)
const raw = items[0].json.output[0].content[0].text;

// Extract JSON inside code block
const jsonMatch = raw.match(/```json([\s\S]*?)```/);

if (!jsonMatch) {
  throw new Error("No JSON found in AI output");
}

// Parse clean JSON text
const jsonString = jsonMatch[1].trim();
const parsed = JSON.parse(jsonString);

// Return parsed JSON as clean structured output
return [
  {
    json: parsed
  }
];
