(() => {
  // Helper: safe JSON parse
  function tryParseJson(s) {
    try {
      return JSON.parse(s);
    } catch (e) {
      return null;
    }
  }

  // 1) get raw output (handles array node, direct node, or nested)
  const candidateSources = [
    // n8n common shapes
    Array.isArray($json) && $json[0] ? $json[0] : null,
    $json,
  ];

  let raw = null;
  for (const cs of candidateSources) {
    if (!cs) continue;
    if (typeof cs.output === 'string' && cs.output.trim()) { raw = cs.output; break; }
    if (cs.email_body_html || cs.email_body_plain || cs.subject) { raw = cs; break; }
    // sometimes the node returns the JSON string in a field named 'summary' or 'text'
    if (typeof cs.summary === 'string' && cs.summary.trim()) { raw = cs.summary; break; }
    if (typeof cs.text === 'string' && cs.text.trim()) { raw = cs.text; break; }
  }

  // 2) If raw is a string that contains a fenced JSON block, extract it
  let parsed = null;
  if (typeof raw === 'string') {
    // extract code-fenced JSON ```json ... ```
    const fenceMatch = raw.match(/```(?:json)?\s*([\s\S]*?)\s*```/i);
    const jsonCandidate = fenceMatch ? fenceMatch[1].trim() : raw.trim();

    // try parse directly if it looks like JSON; otherwise try to find first { ... }
    parsed = tryParseJson(jsonCandidate);
    if (!parsed) {
      const firstBrace = jsonCandidate.indexOf('{');
      const lastBrace = jsonCandidate.lastIndexOf('}');
      if (firstBrace !== -1 && lastBrace !== -1 && lastBrace > firstBrace) {
        const possible = jsonCandidate.slice(firstBrace, lastBrace + 1);
        parsed = tryParseJson(possible);
      }
    }
  } else if (typeof raw === 'object' && raw !== null) {
    parsed = raw;
  }

  // If we still don't have parsed JSON, try $json directly
  if (!parsed) parsed = ($json && Object.keys($json).length ? $json : null);

  // 3) If parsing failed entirely, produce a simple fallback email
  if (!parsed) {
    const fallbackText = String(raw || 'Summary not available').replace(/\n{2,}/g, '\n\n');
    const htmlFallback = `<p>Hello Manager,</p><p>Here is the MR summary (unparsed):</p><pre style="white-space:pre-wrap;border:1px solid #ddd;padding:10px;border-radius:6px;">${escapeHtml(fallbackText)}</pre><p>Regards,<br/>MR Automation System</p>`;
    return htmlFallback;
  }

  // 4) If parsed already contains email_body_html or full HTML, use it directly
  if (parsed.email_body_html && String(parsed.email_body_html).trim()) {
    return String(parsed.email_body_html);
  }

  // 5) Build content from fields (subject, headline, bullets, details, actions, urgency)
  const subject = parsed.subject || parsed.title || `MR Update${parsed.MR || parsed['MR name'] ? `: ${parsed['MR name'] || parsed.MR}` : ''}`;
  const headline = parsed.headline || '';
  const bullets = Array.isArray(parsed.summary_bullets) ? parsed.summary_bullets : (parsed.summary ? (Array.isArray(parsed.summary) ? parsed.summary : String(parsed.summary).split('\n').filter(Boolean)) : []);
  const details = parsed.details_paragraph || parsed.details || parsed.email_body_plain || '';
  const actions = Array.isArray(parsed.action_items) ? parsed.action_items : (parsed.action_items ? [parsed.action_items] : []);
  const urgency = parsed.urgency || parsed.Urgency || '';
  const mrName = parsed['MR name'] || parsed.MR || parsed.MR_ID || '';
  const rawFields = Array.isArray(parsed.raw_fields_used) ? parsed.raw_fields_used : (parsed.raw_fields_used ? [parsed.raw_fields_used] : []);
  const inferred = parsed.inferred_fields || {};

  // Helper: escape html
  function escapeHtml(s) {
    return String(s || '').replace(/[&<>"']/g, function (m) {
      return ({'&':'&amp;','<':'&lt;','>':'&gt;','"':'&quot;',"'":'&#39;'})[m];
    });
  }

  // Build HTML
  let html = '';
  html += `<div style="font-family:Arial,Helvetica,sans-serif;color:#111;line-height:1.45">`;
  html += `<p>Hello Manager,</p>`;
  html += `<h3 style="margin:6px 0 8px 0;font-size:16px">${escapeHtml(subject)}</h3>`;
  if (headline) html += `<p style="margin:4px 0 12px 0"><strong>${escapeHtml(headline)}</strong></p>`;

  // urgency badge
  if (urgency) {
    const color = urgency.toLowerCase() === 'high' ? '#d9534f' : (urgency.toLowerCase() === 'medium' ? '#f0ad4e' : '#5cb85c');
    html += `<p style="margin:6px 0"><strong>Urgency:</strong> <span style="display:inline-block;padding:4px 8px;border-radius:12px;background:${color};color:#fff;font-weight:600;font-size:12px">${escapeHtml(urgency)}</span></p>`;
  }

  // bullets
  if (bullets && bullets.length) {
    html += `<ul style="margin:8px 0 12px 18px">`;
    for (const b of bullets) {
      html += `<li style="margin:6px 0">${escapeHtml(String(b))}</li>`;
    }
    html += `</ul>`;
  }

  // details paragraph
  if (details) {
    html += `<div style="margin:8px 0 12px 0">${escapeHtml(String(details)).replace(/\n/g,'<br/>')}</div>`;
  }

  // action items
  if (actions && actions.length) {
    html += `<p style="margin:6px 0 6px 0"><strong>Action Items:</strong></p>`;
    html += `<ol style="margin:6px 0 12px 22px">`;
    for (const a of actions) html += `<li style="margin:6px 0">${escapeHtml(String(a))}</li>`;
    html += `</ol>`;
  }

  // metadata table (MR, timestamp, raw fields)
  html += `<table style="border-collapse:collapse;font-size:13px;margin-top:8px">`;
  if (mrName) {
    html += `<tr><td style="padding:6px 10px 6px 0;font-weight:600">MR</td><td style="padding:6px 10px">${escapeHtml(mrName)}</td></tr>`;
  }
  if (parsed.Timestamp || parsed.Normalized_Timestamp || inferred.Normalized_Timestamp) {
    const ts = parsed.Normalized_Timestamp || parsed.Timestamp || inferred.Normalized_Timestamp;
    html += `<tr><td style="padding:6px 10px 6px 0;font-weight:600">Timestamp</td><td style="padding:6px 10px">${escapeHtml(ts)}</td></tr>`;
  }
  if (rawFields && rawFields.length) {
    html += `<tr><td style="padding:6px 10px 6px 0;font-weight:600;vertical-align:top">Fields used</td><td style="padding:6px 10px">${escapeHtml(rawFields.join(', '))}</td></tr>`;
  }
  // inferred fields
  if (inferred && Object.keys(inferred).length) {
    html += `<tr><td style="padding:6px 10px 6px 0;font-weight:600;vertical-align:top">Inferred</td><td style="padding:6px 10px">${escapeHtml(Object.entries(inferred).map(([k,v])=>k+': '+v).join('<br/>'))}</td></tr>`;
  }
  html += `</table>`;

  // signature
  html += `<p style="margin-top:18px">Regards,<br/>MR Automation System${mrName ? `<br/><small>Reported by ${escapeHtml(mrName)}</small>` : ''}</p>`;
  html += `</div>`;

  // 6) Also create a plain-text fallback (optional but useful)
  // We return html only because many email nodes use the node output as html body.
  return html;
})()
}}
