parameters.jsCode:
// Receive input from previous node
const items = $input.all();
const results = [];

for (const item of items) {
  let content = item.json.text;

  // Handle case where output is already an object or missing
  if (!content) {
      if (item.json.response) content = item.json.response; // Fallback
      else if (typeof item.json === 'object' && item.json.title) {
          results.push(item.json);
          continue;
      }
  }

  if (typeof content === 'object') {
    results.push(content);
    continue;
  }

  // Remove markdown formatting
  const cleanJson = content.toString().replace(/```json/g, '').replace(/```/g, '').trim();

  // extract JSON object
  const match = cleanJson.match(/\{.*\}/s);
  const jsonStr = match ? match[0] : cleanJson;

  try {
    results.push(JSON.parse(jsonStr));
  } catch (error) {
    // Return raw if parsing fails
    results.push({ 
        raw: content,
        error: error.message 
    });
  }
}

return results;

