// This code checks a list of employees from your Google Sheet
// and finds everyone who has a birthday today.

// This array will hold the employees we find.
const birthdayEmployees = [];

// 1. Get today's date. This is dynamic and changes every day.
const today = new Date();
const currentMonth = today.getMonth(); // 0 for January, 1 for February, etc.
const currentDay = today.getDate();

// 2. Loop through every employee record that comes from the Google Sheets node.
// 'items' is the standard n8n variable for the incoming data.
for (const item of items) {
  // IMPORTANT: Make sure the key here EXACTLY matches your column header in Google Sheets.
  const dobString = item.json["Employee DOB"];

  // We only run the check if a date of birth exists for the employee.
  if (dobString) {
    // 3. Convert the employee's birthday text into a real date object.
    // The JavaScript `new Date()` is smart enough to understand formats like
    // "13-Oct-1990" and "09-June-2004".
    const employeeDob = new Date(dobString);

    // Just to be safe, we check if the date is a valid one.
    if (!isNaN(employeeDob.getTime())) {
      const employeeBirthMonth = employeeDob.getMonth();
      const employeeBirthDay = employeeDob.getDate();

      // 4. THE BIG CHECK: Does the employee's birth month and day match today's?
      if (currentMonth === employeeBirthMonth && currentDay === employeeBirthDay) {
        // It's a match! Add this employee to our birthday list.
        birthdayEmployees.push(item);
      }
    }
  }
}

// 5. Return the result based on whether we found anyone.
if (birthdayEmployees.length > 0) {
  // If anyone has a birthday, return their data to the next node.
  return birthdayEmployees;
} else {
  // If no one has a birthday, return a single item with a special status.
  // This makes it easy to check in the next IF node.
  return [{
    json: {
      status: "NO_BIRTHDAY_TODAY"
    }
  }];
}

// n8n Function node ‚Äî single-item output with status + matches
// REQUIRE: Set node before this with todayIso = {{$today}}

const todayIso = items[0]?.json?.todayIso;
if (!todayIso) return [{ json: { status: "ERROR_MISSING_TODAY", matches: [] } }];

const [TY, TM, TD] = String(todayIso).slice(0,10).split('-').map(Number);
const currentYear = TY, currentMonthIndex = TM - 1, currentDay = TD;

const monthMap = { jan:0,feb:1,mar:2,apr:3,may:4,jun:5,jul:6,aug:7,sep:8,sept:8,oct:9,nov:10,dec:11 };

function normYear(y) {
  y = Number(y);
  if (isNaN(y)) return null;
  if (y < 100) {
    const cutoff = currentYear % 100;
    return y <= cutoff ? 2000 + y : 1900 + y;
  }
  return y;
}

function parseJoin(s) {
  if (!s) return null;
  s = String(s).trim();
  let m = s.match(/^(\d{1,2})[\/\-](\d{1,2})[\/\-](\d{2,4})$/);
  if (m) return { d:+m[1], m:+m[2]-1, y: normYear(m[3]) };
  m = s.match(/^(\d{1,2})[^\w\d]+([A-Za-z]+)[^\w\d]+(\d{2,4})$/);
  if (m) {
    const token = m[2].toLowerCase().replace(/[^a-z]/g,'');
    const mi = monthMap[token.slice(0,3)] ?? monthMap[token];
    return mi !== undefined ? { d:+m[1], m:mi, y: normYear(m[3]) } : null;
  }
  return null;
}

const matches = [];

for (const it of items) {
  const raw =
    it.json?.["Date Joined \n(dd-MMM-yyyy)"] ??
    it["Date Joined \n(dd-MMM-yyyy)"] ??
    it.json?.["Date Joined"] ??
    it["Date Joined"];

  const p = parseJoin(raw);
  if (!p) continue;

  // skip future join dates
  if (p.y > currentYear || (p.y === currentYear && (p.m > currentMonthIndex || (p.m === currentMonthIndex && p.d > currentDay)))) continue;

  if (p.m === currentMonthIndex && p.d === currentDay) {
    const yearsOfService = currentYear - p.y;
    if (yearsOfService >= 1) {
      // build a compact match object (keeps original row fields + years)
      const match = {
        row_number: it.row_number ?? it.json?.row_number ?? null,
        name: it["EMPLOYEE NAME "] ?? it.json?.["EMPLOYEE NAME "] ?? null,
        dateJoinedRaw: raw,
        parsedJoinDate: `${p.y}-${String(p.m+1).padStart(2,'0')}-${String(p.d).padStart(2,'0')}`,
        yearsOfService
      };
      matches.push(match);
    }
  }
}

// single item output: status + matches array
if (matches.length > 0) {
  return [{ json: { status: "ANNIVERSARY_FOUND", matches } }];
}
return [{ json: { status: "NO_ANNIVERSARY_TODAY", matches: [] } }];
