const items = [];

for (const item of $input.all()) {
  const rawOutput = item.json.output;

  // Remove code block markers (e.g., ```json\n ... \n```)
  const cleanedJsonString = rawOutput
    .replace(/^```json\s*/i, '') // remove opening ```json
    .replace(/```$/i, '')        // remove closing ```
    .trim();

  // Parse the cleaned JSON string
  const parsed = JSON.parse(cleanedJsonString);

  // Push structured output to be used downstream
  items.push({ json: parsed });
}

return items;
