// Calculate potential savings by comparing current and alternative vendor prices
// Expected input: query object with currentPrice and alternativePrice

const data = typeof query === 'string' ? JSON.parse(query) : query;
const currentPrice = parseFloat(data.currentPrice);
const alternativePrice = parseFloat(data.alternativePrice);

if (isNaN(currentPrice) || isNaN(alternativePrice)) {
  return 'Error: Invalid price values provided';
}

const savingsAmount = currentPrice - alternativePrice;
const savingsPercentage = ((savingsAmount / currentPrice) * 100).toFixed(2);

const result = {
  currentPrice: currentPrice,
  alternativePrice: alternativePrice,
  savingsAmount: savingsAmount.toFixed(2),
  savingsPercentage: savingsPercentage + '%',
  recommendation: savingsAmount > 0 ? 'Switch to alternative vendor' : 'Keep current vendor'
};

return JSON.stringify(result, null, 2);
