const itemData = $json;
const fromValue = itemData.from?.value?.[0] || {};

const attachments = [];
const parts = itemData.payload?.parts || [];
for (const part of parts) {
  if (part.filename) {
    attachments.push({
      name: part.filename,
      size: part.body?.size || null,
      type: part.mimeType || "unknown"
    });
  }
}

let email = {
  id: itemData.id,
  threadId: itemData.threadId,
  account: itemData.account,

  from: {
    name: fromValue.name || "Unknown",
    email: fromValue.address || "unknown",
    avatar: null
  },

  to: itemData.to?.text || itemData.to?.value?.[0]?.address || "",

  subject: itemData.subject || "(no subject)",
  preview: (itemData.text || "").slice(0, 150),

  body_html: itemData.html || itemData.textAsHtml || "",
  body_text: itemData.text || "",

  date: itemData.date,

  labels: itemData.labelIds || [],

  attachments,

  isRead: !(itemData.labelIds || []).includes("UNREAD"),
  isStarred: false
};

  email = JSON.stringify(email)
  return {email}

