// Get the ChatGPT response
const chatGptOutput = $input.first().json.output[0].content[0].text;

// Extract JSON from markdown code blocks
let complianceData;
try {
  // Remove markdown code blocks (```json and ```)
  const cleanedResponse = chatGptOutput
    .replace(/```json\n?/g, '')
    .replace(/```\n?/g, '')
    .trim();

  // Parse the JSON
  complianceData = JSON.parse(cleanedResponse);

  console.log('‚úÖ Successfully parsed compliance data');
} catch (error) {
  console.error('‚ùå Failed to parse JSON:', error.message);

  // Fallback structure
  complianceData = {
    privacy_policy: { found: false, score: 0, details: "Error parsing response" },
    cookie_consent: { found: false, score: 0, details: "Error parsing response" },
    terms_of_service: { found: false, score: 0, details: "Error parsing response" },
    accessibility: { score: 0, details: "Error parsing response", issues: [] },
    contact_info: { found: false, details: "Error parsing response" },
    ssl_enabled: false,
    overall_score: 0,
    critical_issues: ["Failed to parse compliance data: " + error.message],
    recommendations: ["Please retry the scan"],
    error: true,
    raw_response: chatGptOutput
  };
}

// Get original webhook data
const webhookData = $('Webhook').first().json.body;

// Return enriched data
return {
  json: {
    ...complianceData,
    website_url: webhookData.url,
    company_name: webhookData.company_name,
    email: webhookData.email,
    scan_date: new Date().toISOString(),
    scan_timestamp: Date.now(),
    has_error: complianceData.error || false
  }
};

const data = $input.first().json;

// Helper function to get score color
const getScoreColor = (score) => {
  if (score >= 70) return 'linear-gradient(135deg, #11998e 0%, #38ef7d 100%)';
  if (score >= 40) return 'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)';
  return 'linear-gradient(135deg, #fa709a 0%, #fee140 100%)';
};

// Helper function to get score badge class
const getScoreBadge = (score) => {
  if (score >= 70) return 'score-high';
  if (score >= 40) return 'score-medium';
  return 'score-low';
};

// Format date
const formatDate = (isoDate) => {
  const date = new Date(isoDate);
  return date.toLocaleDateString('en-US', { 
    year: 'numeric', 
    month: 'long', 
    day: 'numeric',
    hour: '2-digit',
    minute: '2-digit'
  });
};

