// Split binary files from Form Trigger into separate items
const binaryData = $input.first().binary;
const jsonData = $input.first().json;

if (!binaryData || Object.keys(binaryData).length === 0) {
  throw new Error('No files uploaded');
}

const output = Object.entries(binaryData).map(([key, fileData]) => {
  return {
    json: {
      ...jsonData,
      binaryKey: key,
      filename: fileData.fileName || key,
      mimeType: fileData.mimeType || 'application/octet-stream'
    },
    binary: {
      data: fileData
    }
  };
});

return output;

// Convert binary to base64 and build Mistral OCR request body
const item = $input.first();
const binaryData = item.binary.data;
const mimeType = item.json.mimeType;
const filename = item.json.filename;

// Get base64 from binary data (n8n stores it in .data property)
const base64Data = binaryData.data;

// Build data URL
const dataUrl = `data:${mimeType};base64,${base64Data}`;

// Determine document type and build request body for Mistral API
const isPdf = mimeType === 'application/pdf' || 
              mimeType === 'application/vnd.openxmlformats-officedocument.wordprocessingml.document' ||
              mimeType === 'application/vnd.openxmlformats-officedocument.presentationml.presentation';

let requestBody;
if (isPdf) {
  requestBody = {
    model: 'mistral-ocr-latest',
    document: {
      type: 'document_url',
      document_url: dataUrl
    }
  };
} else {
  requestBody = {
    model: 'mistral-ocr-latest',
    document: {
      type: 'image_url',
      image_url: dataUrl
    }
  };
}

return [{
  json: {
    filename: filename,
    mimeType: mimeType,
    requestBody: requestBody
  }
}];

// Prepare OCR text for extraction
const ocrResult = $input.first().json;
const originalData = $('Prepare OCR Request').first().json;

let fullText = '';
let pageCount = 1;

if (ocrResult.pages && Array.isArray(ocrResult.pages)) {
  fullText = ocrResult.pages.map(page => page.markdown || '').join('\n\n');
  pageCount = ocrResult.pages.length;
} else if (ocrResult.text) {
  fullText = ocrResult.text;
}

if (!fullText || fullText.trim() === '') {
  fullText = 'No text extracted from document';
}

return [{
  json: {
    ocrText: fullText,
    filename: originalData.filename,
    pageCount: pageCount
  }
}];

// Validate extracted data and assign confidence score
const extracted = $input.first().json;
const metadata = $('Format OCR Text').first().json;

// Required fields for confidence calculation
const requiredFields = ['invoice_number', 'invoice_date', 'vendor_name', 'total_amount'];
let filledCount = 0;
let issues = [];

requiredFields.forEach(field => {
  const value = extracted[field];
  if (value !== null && value !== undefined && value !== '' && value !== 0) {
    filledCount++;
  } else {
    issues.push(`Missing: ${field.replace('_', ' ')}`);
  }
});

// Assign confidence level
let confidence, status;
if (filledCount >= 4) {
  confidence = 'high';
  status = 'Processed';
} else if (filledCount >= 2) {
  confidence = 'medium';
  status = 'Review';
} else {
  confidence = 'low';
  status = 'Error';
}

return [{
  json: {
    invoice_number: extracted.invoice_number || '',
    invoice_date: extracted.invoice_date || '',
    vendor_name: extracted.vendor_name || '',
    vendor_tax_id: extracted.vendor_tax_id || '',
    subtotal: extracted.subtotal || 0,
    tax_rate: extracted.tax_rate || 0,
    tax_amount: extracted.tax_amount || 0,
    total_amount: extracted.total_amount || 0,
    currency: extracted.currency || '',
    filename: metadata.filename,
    confidence: confidence,
    status: status,
    issues: issues.length > 0 ? issues.join('; ') : 'None',
    processed_at: new Date().toISOString(),
    page_count: metadata.pageCount
  }
}];
