const todayData = $input.all()[0].json;
const yesterdayData = $input.all()[1].json;


function percentChange(today, yesterday) {
  today = Number(today);
  yesterday = Number(yesterday);

  if(!yesterday || yesterday === 0) return 0;
  if (yesterday === 0) return 100;
  const change = ((today - yesterday) / yesterday) * 100;
  console.log(change)
  return Number(change.toFixed(1));
}

return {
  users: percentChange(todayData.users, yesterdayData.users),
  session: percentChange(todayData.sessions, yesterdayData.sessions),
  pageViews: percentChange(todayData.screenPageViews, yesterdayData.screenPageViews),
  todayUser: todayData.users,
  yesterdayUser: yesterdayData.users,
  todayDate: todayData.date
}

