// Calculate Sales Velocity for each SKU
// Input: Merged data from inventory, products, orders, sheets
// Output: Items with velocity_7d, velocity_30d, avg_daily_sales

const items = $input.all();

// Extract order data and inventory data
const orders = [];
const inventoryMap = new Map();
const productMap = new Map();
const inventoryMasterMap = new Map();
const suppliersMap = new Map();

// Parse input items to separate orders, inventory, and products
for (const item of items) {
  const data = item.json;

  // Check if this is order data (from Get Last 30 Days Orders)
  if (data.line_items && Array.isArray(data.line_items)) {
    orders.push(data);
  }

  // Check if this is inventory level data (from Get Inventory Levels)
  if (data.inventory_item_id && data.location_id) {
    const inventoryItemId = data.inventory_item_id;
    inventoryMap.set(inventoryItemId, data);
  }

  // Check if this is product data (from Get Product Details)
  if (data.variants && Array.isArray(data.variants)) {
    for (const variant of data.variants) {
      if (variant.sku) {
        productMap.set(variant.sku, {
          ...variant,
          product_title: data.title,
          product_id: data.id,
          product_type: data.product_type,
          vendor: data.vendor
        });
        // Also map by inventory_item_id for cross-referencing
        if (variant.inventory_item_id) {
          productMap.set(variant.inventory_item_id, {
            ...variant,
            product_title: data.title,
            product_id: data.id,
            product_type: data.product_type,
            vendor: data.vendor
          });
        }
      }
    }
  }

  // Check if this is Inventory Master sheet data
  if (data.SKU && (data.reorder_point || data.safety_stock)) {
    inventoryMasterMap.set(data.SKU, data);
  }

  // Check if this is Suppliers sheet data
  if (data.supplier_id || data.supplier_name) {
    const supplierId = data.supplier_id || data.id;
    if (supplierId) {
      suppliersMap.set(supplierId, data);
    }
  }
}

// Calculate sales by SKU
const salesBySku = new Map();
const now = new Date();
const sevenDaysAgo = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000);
const thirtyDaysAgo = new Date(now.getTime() - 30 * 24 * 60 * 60 * 1000);

for (const order of orders) {
  const orderDate = new Date(order.created_at);

  for (const lineItem of order.line_items) {
    const sku = lineItem.sku;
    if (!sku) continue;

    if (!salesBySku.has(sku)) {
      salesBySku.set(sku, {
        total_30d: 0,
        total_7d: 0,
        total_all: 0,
        last_sale_date: null
      });
    }

    const sales = salesBySku.get(sku);
    const quantity = lineItem.quantity || 0;

    sales.total_all += quantity;

    if (orderDate >= thirtyDaysAgo) {
      sales.total_30d += quantity;
    }

    if (orderDate >= sevenDaysAgo) {
      sales.total_7d += quantity;
    }

    // Track last sale date
    if (!sales.last_sale_date || orderDate > new Date(sales.last_sale_date)) {
      sales.last_sale_date = orderDate.toISOString();
    }
  }
}

// Build output with velocity calculations
const outputItems = [];

// Get all unique SKUs from all sources
const allSkus = new Set([
  ...productMap.keys(),
  ...salesBySku.keys(),
  ...inventoryMasterMap.keys()
]);

for (const sku of allSkus) {
  // Skip if this is an inventory_item_id (numeric) and we have the actual SKU
  if (typeof sku === 'number' || /^\d+$/.test(sku)) {
    continue;
  }

  const product = productMap.get(sku) || {};
  const sales = salesBySku.get(sku) || { total_7d: 0, total_30d: 0, total_all: 0, last_sale_date: null };
  const inventoryMaster = inventoryMasterMap.get(sku) || {};

  // Get inventory level data by matching inventory_item_id
  const inventoryItemId = product.inventory_item_id;
  const inventory = inventoryItemId ? inventoryMap.get(inventoryItemId) : {};

  // Calculate velocities
  const velocity_7d = sales.total_7d / 7;
  const velocity_30d = sales.total_30d / 30;
  const avg_daily_sales = velocity_30d; // Use 30-day as primary average

  // Get supplier data if available
  const supplierId = inventoryMaster.supplier_id || product.vendor;
  const supplier = supplierId ? suppliersMap.get(supplierId) : {};

  outputItems.push({
    json: {
      sku: sku,
      inventory_item_id: inventoryItemId,
      location_id: inventory.location_id,
      available_quantity: inventory.available,
      current_stock: inventory.available || 0,
      product_title: product.product_title,
      product_id: product.product_id,
      product_type: product.product_type,
      vendor: product.vendor,
      variant_id: product.id,
      price: product.price,
      sales_7d: sales.total_7d,
      sales_30d: sales.total_30d,
      velocity_7d: Math.round(velocity_7d * 100) / 100,
      velocity_30d: Math.round(velocity_30d * 100) / 100,
      avg_daily_sales: Math.round(avg_daily_sales * 100) / 100,
      last_sale_date: sales.last_sale_date,
      // Include inventory master data
      safety_stock: inventoryMaster.safety_stock || 0,
      max_stock: inventoryMaster.max_stock,
      supplier_id: inventoryMaster.supplier_id || supplierId,
      // Include supplier data
      supplier_name: supplier.supplier_name || supplier.name,
      supplier_email: supplier.supplier_email || supplier.email,
      lead_time_days: supplier.lead_time_days || 7,
      moq: supplier.moq || supplier.minimum_order_quantity || 1,
      unit_cost: supplier.unit_cost || product.price || 0,
      calculated_at: now.toISOString()
    }
  });
}

return outputItems;

// Calculate Dynamic Reorder Point
// Formula: reorder_point = avg_daily_sales * lead_time + safety_stock

const item = $input.item.json;

// Get avg_daily_sales from previous Calculate Sales Velocity node
const avg_daily_sales = item.avg_daily_sales || 0;

// Get lead_time from supplier data (merged from Read Suppliers node)
const lead_time = item.lead_time_days || item.supplier_lead_time || 7; // default 7 days

// Get safety_stock from workflow config
const safetyStockDays = $('Workflow Configuration').first().json.safetyStockDays || 7;
const safety_stock = avg_daily_sales * safetyStockDays;

// Calculate reorder point
const reorder_point = Math.ceil(avg_daily_sales * lead_time + safety_stock);

// Get current stock level
const current_stock = item.current_stock || item.available_quantity || item.inventory_quantity || 0;

// Determine if reorder is needed
const needs_reorder = current_stock <= reorder_point;

// Return enriched item with reorder calculations
return {
  ...item,
  reorder_point: reorder_point,
  current_stock: current_stock,
  needs_reorder: needs_reorder,
  avg_daily_sales: avg_daily_sales,
  lead_time_days: lead_time,
  safety_stock: safety_stock,
  days_until_stockout: avg_daily_sales > 0 ? Math.floor(current_stock / avg_daily_sales) : 999,
  recommended_order_qty: needs_reorder ? Math.ceil(reorder_point - current_stock) : 0
};

// Multi-Warehouse Distribution Logic
// Analyzes inventory across warehouses to identify transfer opportunities

const items = $input.all();
const transferRecommendations = [];

// Group inventory by SKU and warehouse
const inventoryBySku = {};

for (const item of items) {
  const sku = item.json.sku;
  const warehouse = item.json.warehouse || 'main';
  const currentStock = item.json.current_stock || 0;
  const reorderPoint = item.json.reorder_point || 0;
  const maxStock = item.json.max_stock || reorderPoint * 3;

  if (!inventoryBySku[sku]) {
    inventoryBySku[sku] = {};
  }

  inventoryBySku[sku][warehouse] = {
    current_stock: currentStock,
    reorder_point: reorderPoint,
    max_stock: maxStock,
    overstock: Math.max(0, currentStock - maxStock),
    understock: Math.max(0, reorderPoint - currentStock)
  };
}

// Analyze each SKU for transfer opportunities
for (const sku in inventoryBySku) {
  const warehouses = inventoryBySku[sku];
  const warehouseNames = Object.keys(warehouses);

  // Compare each pair of warehouses
  for (let i = 0; i < warehouseNames.length; i++) {
    for (let j = 0; j < warehouseNames.length; j++) {
      if (i === j) continue;

      const fromWarehouse = warehouseNames[i];
      const toWarehouse = warehouseNames[j];
      const from = warehouses[fromWarehouse];
      const to = warehouses[toWarehouse];

      // Check if warehouse A has overstock and warehouse B has understock
      if (from.overstock > 0 && to.understock > 0) {
        // Calculate optimal transfer quantity
        // Transfer the minimum of: overstock amount, understock need, or safe transfer amount
        const safeTransferAmount = Math.floor(from.current_stock - from.reorder_point);
        const transferQuantity = Math.min(
          from.overstock,
          to.understock,
          Math.max(0, safeTransferAmount)
        );

        if (transferQuantity > 0) {
          transferRecommendations.push({
            json: {
              sku: sku,
              from_warehouse: fromWarehouse,
              to_warehouse: toWarehouse,
              transfer_quantity: transferQuantity,
              from_current_stock: from.current_stock,
              from_after_transfer: from.current_stock - transferQuantity,
              to_current_stock: to.current_stock,
              to_after_transfer: to.current_stock + transferQuantity,
              priority: to.understock / to.reorder_point, // Higher priority for more critical understocks
              estimated_cost_savings: transferQuantity * 10, // Placeholder for cost calculation
              recommendation_date: new Date().toISOString()
            }
          });
        }
      }
    }
  }
}

// Sort by priority (highest first)
transferRecommendations.sort((a, b) => b.json.priority - a.json.priority);

return transferRecommendations.length > 0 ? transferRecommendations : [{ json: { message: 'No transfer recommendations at this time' } }];

// Prepare PO Email Context
// Group line items by supplier, calculate totals, format supplier details

const items = $input.all();
const posBySupplier = {};

// Group items by supplier
for (const item of items) {
  const data = item.json;
  const supplierId = data.supplier_id || data.supplierId;
  const supplierName = data.supplier_name || data.supplierName || 'Unknown Supplier';
  const supplierEmail = data.supplier_email || data.supplierEmail || '';
  const supplierApiEndpoint = data.supplier_api_endpoint || data.supplierApiEndpoint || '';

  if (!posBySupplier[supplierId]) {
    posBySupplier[supplierId] = {
      supplier_id: supplierId,
      supplier_name: supplierName,
      supplier_email: supplierEmail,
      supplier_api_endpoint: supplierApiEndpoint,
      line_items: [],
      total_amount: 0
    };
  }

  // Add line item
  const lineItem = {
    sku: data.sku || data.product_sku,
    product_name: data.product_name || data.productName,
    quantity: data.order_quantity || data.orderQuantity || 0,
    unit_price: data.unit_cost || data.unitCost || 0,
    line_total: (data.order_quantity || 0) * (data.unit_cost || 0)
  };

  posBySupplier[supplierId].line_items.push(lineItem);
  posBySupplier[supplierId].total_amount += lineItem.line_total;
}

// Convert to array and generate PO numbers
const result = [];
let poCounter = 1;
const now = new Date();
const dateStr = now.toISOString().split('T')[0].replace(/-/g, '');

for (const supplierId in posBySupplier) {
  const po = posBySupplier[supplierId];
  po.po_number = `PO-${dateStr}-${String(poCounter).padStart(4, '0')}`;
  po.po_date = now.toISOString().split('T')[0]; // Add PO date in YYYY-MM-DD format
  po.total_amount = Math.round(po.total_amount * 100) / 100; // Round to 2 decimals

  // Format line items as HTML table rows
  const lineItemsHtml = po.line_items.map(item => {
    return `<tr>
      <td>${item.sku}</td>
      <td>${item.product_name}</td>
      <td>${item.quantity}</td>
      <td>$${item.unit_price.toFixed(2)}</td>
      <td>$${item.line_total.toFixed(2)}</td>
    </tr>`;
  }).join('\n');

  po.line_items = lineItemsHtml;

  // Add default delivery instructions
  po.delivery_instructions = 'Please deliver to our main warehouse during business hours (9 AM - 5 PM, Monday-Friday). Contact receiving department at least 24 hours before delivery.';

  result.push({ json: po });
  poCounter++;
}

return result;

// Detect slow-moving inventory items and generate actionable recommendations

const items = $input.all();
const slowMovers = [];

// Configuration
const SLOW_MOVER_THRESHOLD = 2; // velocity below this is considered slow
const today = new Date();

for (const item of items) {
  const velocity = item.json.velocity_30d || 0;
  const lastSaleDate = item.json.last_sale_date ? new Date(item.json.last_sale_date) : null;

  // Check if item is a slow mover
  if (velocity < SLOW_MOVER_THRESHOLD) {
    // Calculate days since last sale
    let daysSinceLastSale = 0;
    if (lastSaleDate) {
      const diffTime = Math.abs(today - lastSaleDate);
      daysSinceLastSale = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    } else {
      daysSinceLastSale = 90; // Default if no sale date available
    }

    // Calculate suggested discount based on days without sales
    let suggestedDiscount = 10; // Base discount
    if (daysSinceLastSale > 60) {
      suggestedDiscount = 30;
    } else if (daysSinceLastSale > 30) {
      suggestedDiscount = 20;
    } else if (daysSinceLastSale > 14) {
      suggestedDiscount = 15;
    }

    // Suggest bundle partners based on category
    const category = item.json.category || item.json.product_type || 'General';
    const sku = item.json.sku || item.json.product_id;

    // Find potential bundle partners from same category with better velocity
    const bundlePartners = items
      .filter(i => {
        const iCategory = i.json.category || i.json.product_type || 'General';
        const iVelocity = i.json.velocity_30d || 0;
        const iSku = i.json.sku || i.json.product_id;
        return iCategory === category && iVelocity > velocity && iSku !== sku;
      })
      .slice(0, 3)
      .map(i => i.json.sku || i.json.product_id || i.json.title)
      .join(', ');

    slowMovers.push({
      json: {
        ...item.json,
        days_no_sales: daysSinceLastSale,
        suggested_discount: suggestedDiscount,
        suggested_bundle: bundlePartners || 'No suitable bundle partners found',
        slow_mover_flag: true,
        recommendation_date: today.toISOString()
      }
    });
  }
}

// Return slow movers or empty array if none found
return slowMovers.length > 0 ? slowMovers : [{ json: { message: 'No slow-moving items detected' } }];

// Optimize Profit Priority
// Calculate profit score and prioritize SKUs based on profitability

const items = $input.all();
const optimizedItems = [];

for (const item of items) {
  const data = item.json;

  // Extract relevant metrics with defaults - using correct field names from previous nodes
  const unitPrice = data.unit_price || data.unit_cost || 0;
  const unitCost = data.unit_cost || data.supplier_unit_cost || unitPrice * 0.6; // Estimate if not available
  const margin = unitPrice > 0 ? (unitPrice - unitCost) / unitPrice : 0;

  // Use velocity_30d from Calculate Sales Velocity node
  const velocity = data.velocity_30d || data.avg_daily_sales || 0;

  // Use carrying_cost if available, otherwise calculate as percentage of unit cost
  const carryingCost = data.carrying_cost || (data.current_stock * unitCost * 0.25) || 0.01;

  // Use stockout_risk from Calculate Stockout Risk node
  const stockoutRisk = data.stockout_risk || 0.01;

  // Calculate profit score: (margin * velocity) / (carrying_cost + stockout_risk)
  // Higher margin and velocity increase score, higher costs and risk decrease it
  const profitScore = velocity > 0 ? (margin * velocity) / (carryingCost + stockoutRisk) : 0;

  optimizedItems.push({
    ...data,
    profit_score: Math.round(profitScore * 100) / 100,
    margin: Math.round(margin * 10000) / 100, // Convert to percentage with 2 decimals
    velocity: velocity,
    carrying_cost: Math.round(carryingCost * 100) / 100,
    stockout_risk: Math.round(stockoutRisk * 100) / 100
  });
}

// Sort by profit_score descending (highest profit priority first)
optimizedItems.sort((a, b) => b.profit_score - a.profit_score);

// Add priority rank
const rankedItems = optimizedItems.map((item, index) => ({
  json: {
    ...item,
    priority_rank: index + 1,
    priority_tier: index < optimizedItems.length * 0.2 ? 'High' : 
                   index < optimizedItems.length * 0.5 ? 'Medium' : 'Low'
  }
}));

return rankedItems;

// Calculate Stockout Risk
// Assess risk level based on days until stockout vs lead time

const item = $input.item.json;

// Get days until stockout and lead time
const daysUntilStockout = item.days_until_stockout || 999;
const leadTime = item.lead_time_days || 7;

// Calculate stockout risk score (0-1 scale)
let stockoutRisk = 0;

if (daysUntilStockout <= leadTime) {
  // Critical: Will stockout before next order arrives
  stockoutRisk = 0.9;
} else if (daysUntilStockout <= leadTime * 1.5) {
  // High: Very close to lead time threshold
  stockoutRisk = 0.7;
} else if (daysUntilStockout <= leadTime * 2) {
  // Medium: Some buffer but still concerning
  stockoutRisk = 0.5;
} else if (daysUntilStockout <= leadTime * 3) {
  // Low-Medium: Reasonable buffer
  stockoutRisk = 0.3;
} else {
  // Low: Plenty of stock
  stockoutRisk = 0.1;
}

// Return item with stockout risk score
return {
  ...item,
  stockout_risk: stockoutRisk
};

// Enrich with Supplier Data
// Match items with supplier information and add supplier fields

const items = $input.all();
const suppliers = $('Read Suppliers').all();
const supplierMap = new Map();

// Build supplier lookup map with multiple ID field variations
for (const supplier of suppliers) {
  const data = supplier.json;
  const supplierId = data.supplier_id || data.id || data.supplierId || data.ID;

  if (supplierId) {
    supplierMap.set(String(supplierId).trim(), data);
  }
}

// Enrich each item with supplier data
const enrichedItems = items.map(item => {
  const itemData = item.json;

  // Try multiple supplier ID field variations
  const supplierId = String(
    itemData.supplier_id || 
    itemData.preferred_supplier_id || 
    itemData.supplierId || 
    itemData.preferredSupplierId ||
    ''
  ).trim();

  // Get supplier data from map
  const supplier = supplierMap.get(supplierId) || {};

  return {
    json: {
      ...itemData,
      supplier_id: supplierId || itemData.supplier_id,
      supplier_name: supplier.supplier_name || supplier.name || supplier.supplierName || 'Unknown Supplier',
      supplier_email: supplier.supplier_email || supplier.email || supplier.supplierEmail || '',
      supplier_api_endpoint: supplier.api_endpoint || supplier.apiEndpoint || supplier.supplier_api_endpoint || '',
      supplier_available: supplier.available !== false && supplier.is_active !== false,
      lead_time_days: supplier.lead_time_days || supplier.leadTimeDays || supplier.lead_time || 7,
      moq: supplier.moq || supplier.minimum_order_quantity || supplier.minimumOrderQuantity || supplier.MOQ || 1,
      unit_cost: supplier.unit_cost || supplier.unitCost || supplier.cost || itemData.unit_price || itemData.unitPrice || 0
    }
  };
});

return enrichedItems;

// Calculate Order Quantity and Value
// Ensures MOQ is met and calculates total PO value

const item = $input.item.json;

// Get recommended order quantity
const orderQuantity = item.recommended_order_qty || 0;

// Get unit cost from supplier data
const unitCost = item.unit_cost || item.unit_price || 0;

// Get minimum order quantity
const moq = item.moq || 1;

// Adjust quantity to meet MOQ
const adjustedQuantity = Math.max(orderQuantity, moq);

// Calculate total PO value
const totalPoValue = adjustedQuantity * unitCost;

// Get current timestamp
const now = new Date();

// Return enriched item with order calculations
return {
  ...item,
  order_quantity: adjustedQuantity,
  unit_price: unitCost,
  total_po_value: Math.round(totalPoValue * 100) / 100,
  po_date: now.toISOString(),
  created_at: now.toISOString(),
  status: 'pending'
};

// Aggregate Daily Summary with safe error handling

let totalPos = 0;
let totalValue = 0;
let criticalAlerts = 0;
let slowMovers = 0;

// Safely get PO items
try {
  const poItems = $('Update Purchase Order Log').all();
  for (const po of poItems) {
    totalPos++;
    totalValue += po.json.total_amount || 0;
  }
} catch (error) {
  console.log('Update Purchase Order Log node not executed or has no data');
}

// Safely get critical alerts count
try {
  const criticalAlertItems = $('Alert Critical Stock Risk').all();
  criticalAlerts = criticalAlertItems.length;
} catch (error) {
  console.log('Alert Critical Stock Risk node not executed or has no data');
}

// Safely get slow movers count
try {
  const slowMoverItems = $('Alert Slow-Mover Suggestions').all();
  slowMovers = slowMoverItems.length;
} catch (error) {
  console.log('Alert Slow-Mover Suggestions node not executed or has no data');
}

return [{
  json: {
    total_pos: totalPos,
    total_value: Math.round(totalValue * 100) / 100,
    critical_alerts: criticalAlerts,
    slow_movers_count: slowMovers,
    summary_date: new Date().toISOString()
  }
}];
