// Predict Abandonment Reason - Merge cart and customer data
const items = $input.all();
const results = [];

// Group items by cart_id to merge cart details and customer history
const cartMap = new Map();

for (const item of items) {
  const cartId = item.json.cart_id;

  if (!cartMap.has(cartId)) {
    cartMap.set(cartId, {
      cartData: {},
      customerData: {},
      originalData: {}
    });
  }

  const entry = cartMap.get(cartId);

  // Check if this item has cart details (from Fetch Cart Details node)
  if (item.json.items || item.json.line_items) {
    entry.cartData = item.json;
  }
  // Check if this item has customer history (from Fetch Customer History node)
  else if (item.json.orders || item.json.total_spent !== undefined) {
    entry.customerData = item.json;
  }
  // Otherwise it's the original normalized data
  else {
    entry.originalData = item.json;
  }
}

// Process each cart
for (const [cartId, data] of cartMap.entries()) {
  const original = data.originalData;
  const cart = data.cartData;
  const customer = data.customerData;

  // Calculate cart metrics
  const cartItems = cart.items || cart.line_items || original.products || [];
  const cartValue = original.subtotal || cart.total_price || 0;
  const itemCount = Array.isArray(cartItems) ? cartItems.length : 0;

  // Calculate customer metrics
  const orderHistory = customer.orders || [];
  const orderCount = Array.isArray(orderHistory) ? orderHistory.length : (customer.orders_count || 0);
  const totalSpent = customer.total_spent || 0;
  const avgOrderValue = orderCount > 0 ? totalSpent / orderCount : 0;

  // Simple rule-based abandonment prediction
  let predictedReason = 'Just Browsing';
  let confidence = 60;
  let explanation = 'Customer may be comparing options';
  let recommendedAction = 'Send gentle reminder with product highlights';

  if (cartValue > 200) {
    predictedReason = 'Price Sensitivity';
    confidence = 75;
    explanation = 'High cart value may cause hesitation';
    recommendedAction = 'Offer discount or payment plan';
  } else if (orderCount === 0) {
    predictedReason = 'Checkout Complexity';
    confidence = 70;
    explanation = 'First-time customer unfamiliar with checkout';
    recommendedAction = 'Simplify checkout messaging, offer support';
  } else if (original.device === 'mobile') {
    predictedReason = 'Technical Issues';
    confidence = 65;
    explanation = 'Mobile checkout may have usability challenges';
    recommendedAction = 'Send mobile-optimized reminder';
  } else if (itemCount > 5) {
    predictedReason = 'Comparison Shopping';
    confidence = 68;
    explanation = 'Multiple items suggest research phase';
    recommendedAction = 'Highlight bestsellers and reviews';
  }

  results.push({
    json: {
      ...original,
      cart_id: cartId,
      cartDetails: cart,
      customerHistory: customer,
      cartValue: cartValue,
      itemCount: itemCount,
      orderCount: orderCount,
      totalSpent: totalSpent,
      avgOrderValue: avgOrderValue,
      predictedReason: predictedReason,
      confidence: confidence,
      explanation: explanation,
      recommendedAction: recommendedAction,
      predictedAt: new Date().toISOString()
    }
  });
}

return results;

// Personalization Engine - Generate personalized offers and segment customers
const items = $input.all();
const results = [];

for (const item of items) {
  const data = item.json;

  const orderCount = data.orderCount || 0;
  const totalSpent = data.totalSpent || 0;
  const cartValue = data.cartValue || 0;

  // Classify customer segment
  let customerSegment = 'new';
  if (orderCount === 0) {
    customerSegment = 'new';
  } else if (orderCount >= 5 || totalSpent >= 1000) {
    customerSegment = 'vip';
  } else {
    customerSegment = 'returning';
  }

  // Calculate dynamic discount
  let discountPercent = 10;
  let discountCode = 'CART10';

  if (customerSegment === 'new') {
    if (cartValue >= 100) {
      discountPercent = 15;
      discountCode = 'WELCOME15';
    } else {
      discountPercent = 10;
      discountCode = 'WELCOME10';
    }
  } else if (customerSegment === 'returning') {
    if (cartValue >= 150) {
      discountPercent = 12;
      discountCode = 'RETURN12';
    } else {
      discountPercent = 8;
      discountCode = 'RETURN8';
    }
  } else if (customerSegment === 'vip') {
    if (cartValue >= 200) {
      discountPercent = 20;
      discountCode = 'VIP20';
    } else {
      discountPercent = 15;
      discountCode = 'VIP15';
    }
  }

  // A/B test group
  const abGroup = Math.random() < 0.5 ? 'A' : 'B';

  // Extract customer name
  const customerName = data.customer_name || data.customerHistory?.first_name || 'Valued Customer';
  const customerEmail = data.email || data.customer_email || '';
  const customerPhone = data.phone || data.customer_phone || '';

  // Build checkout URL
  const shopifyDomain = $('Workflow Configuration').first().json.shopifyDomain || 'shop.example.com';
  const checkoutUrl = `https://${shopifyDomain}/cart/${data.cart_id}`;

  results.push({
    json: {
      ...data,
      customerSegment: customerSegment,
      discountPercent: discountPercent,
      discountCode: discountCode,
      abGroup: abGroup,
      customerName: customerName,
      customerEmail: customerEmail,
      customerPhone: customerPhone,
      checkoutUrl: checkoutUrl,
      personalizedAt: new Date().toISOString()
    }
  });
}

return results;

