// Merge Attribution - SMS: Add SMS touchpoint to journey
const items = $input.all();
const results = [];

for (const item of items) {
  const data = item.json;

  // Get existing journey touchpoints or initialize
  const journeyTouchpoints = data.journey_touchpoints || [];

  // Add SMS touchpoint
  journeyTouchpoints.push({
    id: 'sms',
    type: 'sms',
    timestamp: new Date().toISOString(),
    status: 'sent',
    discount_code: data.discountCode,
    discount_percent: data.discountPercent
  });

  results.push({
    json: {
      ...data,
      journey_touchpoints: journeyTouchpoints,
      attribution_touchpoint: 'sms',
      attribution_timestamp: new Date().toISOString(),
      attribution_channel: 'sms',
      attribution_sequence: journeyTouchpoints.length,
      last_touchpoint: 'sms'
    }
  });
}

return results;

