// Hash Customer Data for Facebook Custom Audience API
const crypto = require('crypto');

const item = $input.item.json;

// Function to hash data with SHA256
function hashSHA256(data) {
  if (!data) return '';
  return crypto.createHash('sha256').update(data.toLowerCase().trim()).digest('hex');
}

// Hash email and phone
const hashedEmail = hashSHA256(item.customerEmail || item.email || '');
const hashedPhone = hashSHA256(item.customerPhone || item.phone || '');

return {
  json: {
    ...item,
    hashedEmail: hashedEmail,
    hashedPhone: hashedPhone,
    facebookPayload: {
      schema: ['EMAIL', 'PHONE'],
      data: [[hashedEmail, hashedPhone]]
    },
    hashedAt: new Date().toISOString()
  }
};
