const apifyResponse = $input.first().json;
const posts = apifyResponse.data?.items || [];

if (posts.length === 0) {
  return [{ json: { error: 'No posts found', username: $('Influencer Audit Form').item.json.username } }];
}

// Get profile info from first post
const firstPost = posts[0];
const followers = firstPost.ownerFollowersCount || 1;
const username = firstPost.ownerUsername || $('Influencer Audit Form').item.json.username;

// Calculate total engagement
let totalEngagement = 0;
posts.forEach(post => {
  const likes = post.likesCount || 0;
  const comments = post.commentsCount || 0;
  totalEngagement += likes + comments;
});

// Calculate averages
const avgEngagement = Math.round(totalEngagement / posts.length);
const engagementRate = ((avgEngagement / followers) * 100).toFixed(2);

// Determine health status based on thresholds
const lowThreshold = $('Workflow Configuration').item.json.engagementThresholdLow;
const highThreshold = $('Workflow Configuration').item.json.engagementThresholdHigh;

let status = 'Healthy';
if (parseFloat(engagementRate) < lowThreshold) {
  status = 'Suspicious (Low Engagement - Possible Fake Followers)';
} else if (parseFloat(engagementRate) > highThreshold && followers > 10000) {
  status = 'Suspicious (Unusually High - Possible Bot Activity)';
}

// Return aggregated data with all posts for next node
return posts.map(post => ({
  json: {
    ...post,
    username: username,
    followers: followers,
    engagementRate: engagementRate + '%',
    avgEngagement: avgEngagement,
    status: status,
    totalPosts: posts.length
  }
}));

