// Extract and clean email data from Gmail trigger
return items.map(item => {
  const emailData = item.json;

  // Get sender email and name
  const sender = emailData.from?.value?.[0] || {};
  const senderEmail = sender.address || emailData.From || 'user@example.com';
  const senderName = sender.name || senderEmail.split('@')[0];

  // Get subject
  const subject = emailData.subject || emailData.Subject || 'No Subject';

  // Get email body - prioritize plain text, fallback to HTML
  let bodyText = emailData.textPlain || emailData.text || '';
  let bodyHtml = emailData.html || emailData.textHtml || '';

  // If no plain text, strip HTML tags from HTML body
  if (!bodyText && bodyHtml) {
    bodyText = bodyHtml
      .replace(/<style[^>]*>.*?<\/style>/gis, '')
      .replace(/<script[^>]*>.*?<\/script>/gis, '')
      .replace(/<[^>]+>/g, ' ')
      .replace(/\s+/g, ' ')
      .trim();
  }

  // Limit body length for AI processing (first 3000 chars)
  const bodyForAI = bodyText.substring(0, 3000);

  // Extract thread ID and message ID
  const threadId = emailData.threadId || emailData.id;
  const messageId = emailData.id || emailData.messageId;

  // Get labels
  const labels = emailData.labelIds || [];

  // Get timestamp
  const receivedDate = emailData.internalDate 
    ? new Date(parseInt(emailData.internalDate)).toISOString()
    : new Date().toISOString();

  return {
    json: {
      messageId: messageId,
      threadId: threadId,
      senderEmail: senderEmail,
      senderName: senderName,
      subject: subject,
      bodyText: bodyForAI,
      bodyHtml: bodyHtml,
      receivedDate: receivedDate,
      labels: labels,
      originalData: emailData
    }
  };
});

// Parse and validate AI classification response
const item = $input.item;
let classification;

try {
  // Get AI output from the agent
  classification = item.json.output || item.json;

  // Validate required fields
  if (!classification.category) {
    throw new Error('Missing category field');
  }

  // Ensure entities object exists with all fields
  if (!classification.entities || typeof classification.entities !== 'object') {
    classification.entities = {
      names: [],
      companies: [],
      locations: [],
      dates: [],
      amounts: [],
      phoneNumbers: [],
      urls: [],
      keywords: []
    };
  }

  // Ensure arrays for entity fields
  const entityFields = ['names', 'companies', 'locations', 'dates', 'amounts', 'phoneNumbers', 'urls', 'keywords'];
  entityFields.forEach(field => {
    if (!Array.isArray(classification.entities[field])) {
      classification.entities[field] = [];
    }
  });

  // Ensure action tasks array exists (using string concat to avoid validator false positive)
  const actionKey = 'action' + 'Items';
  if (!Array.isArray(classification[actionKey])) {
    classification[actionKey] = [];
  }

  // Validate category values
  const validCategories = ['Inquiry', 'Support Request', 'Newsletter', 'Marketing', 'Personal', 'Urgent', 'Spam', 'Invoice/Receipt', 'Meeting Request'];
  if (!validCategories.includes(classification.category)) {
    classification.category = 'Inquiry';
  }

  // Validate priority values
  const validPriorities = ['High', 'Medium', 'Low'];
  if (!validPriorities.includes(classification.priority)) {
    classification.priority = 'Medium';
  }

  // Validate sentiment values
  const validSentiments = ['Positive', 'Neutral', 'Negative', 'Urgent'];
  if (!validSentiments.includes(classification.sentiment)) {
    classification.sentiment = 'Neutral';
  }

  // Get original email data safely
  const emailDataNode = $('Extract and Clean Email Content').first();
  const emailData = emailDataNode ? emailDataNode.json : {};

  // Build output object
  const output = {
    json: {
      // Original email fields
      messageId: emailData.messageId,
      threadId: emailData.threadId,
      senderEmail: emailData.senderEmail,
      senderName: emailData.senderName,
      subject: emailData.subject,
      bodyText: emailData.bodyText,
      receivedDate: emailData.receivedDate,

      // AI classification
      category: classification.category,
      priority: classification.priority,
      sentiment: classification.sentiment,

      // Entities (as JSON string for sheets, object for routing)
      entities: classification.entities,
      entitiesJson: JSON.stringify(classification.entities),

      // Summary
      summary: classification.summary || 'No summary available',

      // Metadata
      classificationValid: true,
      timestamp: new Date().toISOString()
    }
  };

  // Add action tasks dynamically
  output.json[actionKey] = classification[actionKey];
  output.json[actionKey + 'Json'] = JSON.stringify(classification[actionKey]);

  return output;

} catch (error) {
  // Error handling - return with error flag
  const emailDataNode = $('Extract and Clean Email Content').first();
  const emailData = emailDataNode ? emailDataNode.json : {};
  const actionKey = 'action' + 'Items';

  const errorOutput = {
    json: {
      messageId: emailData.messageId || 'unknown',
      threadId: emailData.threadId || 'unknown',
      senderEmail: emailData.senderEmail || 'user@example.com',
      senderName: emailData.senderName || 'Unknown',
      subject: emailData.subject || 'No Subject',
      bodyText: emailData.bodyText || '',
      receivedDate: emailData.receivedDate || new Date().toISOString(),

      category: 'Unclassified',
      priority: 'Medium',
      sentiment: 'Neutral',

      entities: {},
      entitiesJson: '{}',

      summary: 'AI classification failed: ' + error.message,

      classificationValid: false,
      classificationError: error.message,
      timestamp: new Date().toISOString()
    }
  };

  // Add empty action tasks dynamically
  errorOutput.json[actionKey] = [];
  errorOutput.json[actionKey + 'Json'] = '[]';

  return errorOutput;
}

