// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  const url = item.json.linkedin_url || "";

  // Add trailing slash if missing
  item.json.linkedin_url = url.endsWith("/") ? url : url + "/";

  // Or, if you truly want a new field instead of overwriting:
  // item.json.myNewField = url.endsWith("/") ? url : url + "/";
}

return $input.all();
