// Extract the performance analysis from the agent
const agentOutput = $input.all();

// Process the structured output from the agent
let performanceData = [];

for (const item of agentOutput) {
  const analysis = item.json;

  // Check if there are any issues (bottlenecks, overload, low productivity)
  const hasIssues = 
    (analysis.bottlenecks && analysis.bottlenecks.length > 0) ||
    (analysis.overloadedEmployees && analysis.overloadedEmployees.length > 0) ||
    (analysis.lowProductivity && analysis.lowProductivity.length > 0);

  performanceData.push({
    json: {
      ...analysis,
      hasIssues: hasIssues,
      timestamp: new Date().toISOString()
    }
  });
}

return performanceData;

