// Calculate churn signals for all customers
const enrichedCustomers = [];

for (const item of $input.all()) {
  const customer = item.json;

  // Calculate days since last activity
  const lastActivityDate = customer.last_activity_date ? new Date(customer.last_activity_date) : null;
  const daysSinceLastActivity = lastActivityDate ? 
    Math.floor((new Date() - lastActivityDate) / (1000 * 60 * 60 * 24)) : 999;

  // Calculate purchase recency (days since last purchase)
  const lastPurchaseDate = customer.last_purchase_date ? new Date(customer.last_purchase_date) : null;
  const purchaseRecency = lastPurchaseDate ? 
    Math.floor((new Date() - lastPurchaseDate) / (1000 * 60 * 60 * 24)) : 999;

  // Determine subscription status
  const subscriptionStatus = customer.subscription_status || 'unknown';
  const isActiveSubscription = subscriptionStatus === 'active';

  // Calculate engagement score (0-100)
  const loginCount = customer.login_count || 0;
  const emailOpenRate = customer.email_open_rate || 0;
  const featureUsage = customer.feature_usage_count || 0;

  const engagementScore = Math.min(100, 
    (loginCount * 2) + 
    (emailOpenRate * 50) + 
    (featureUsage * 3)
  );

  // Calculate overall churn risk score (0-100, higher = more risk)
  let churnRiskScore = 0;

  // Factor 1: Days since last activity (max 40 points)
  if (daysSinceLastActivity > 90) churnRiskScore += 40;
  else if (daysSinceLastActivity > 60) churnRiskScore += 30;
  else if (daysSinceLastActivity > 30) churnRiskScore += 20;
  else if (daysSinceLastActivity > 14) churnRiskScore += 10;

  // Factor 2: Purchase recency (max 25 points)
  if (purchaseRecency > 180) churnRiskScore += 25;
  else if (purchaseRecency > 120) churnRiskScore += 20;
  else if (purchaseRecency > 90) churnRiskScore += 15;
  else if (purchaseRecency > 60) churnRiskScore += 10;

  // Factor 3: Subscription status (max 15 points)
  if (!isActiveSubscription) churnRiskScore += 15;
  else if (subscriptionStatus === 'trial') churnRiskScore += 10;

  // Factor 4: Engagement score (max 20 points, inverse)
  if (engagementScore < 20) churnRiskScore += 20;
  else if (engagementScore < 40) churnRiskScore += 15;
  else if (engagementScore < 60) churnRiskScore += 10;
  else if (engagementScore < 80) churnRiskScore += 5;

  // Ensure score is between 0-100
  churnRiskScore = Math.min(100, Math.max(0, churnRiskScore));

  // Enrich customer data with churn signals
  enrichedCustomers.push({
    json: {
      ...customer,
      churn_signals: {
        days_since_last_activity: daysSinceLastActivity,
        purchase_recency: purchaseRecency,
        subscription_status: subscriptionStatus,
        engagement_score: engagementScore,
        churn_risk_score: churnRiskScore
      },
      churn_risk_score: churnRiskScore
    }
  });
}

return enrichedCustomers;

