// Analyze campaign performance metrics
const items = $input.all();

// Initialize metrics
let totalCampaigns = items.length;
let conversions = 0;
let responses = 0;
let offerTypes = {};
let successfulPatterns = [];

// Process each campaign result
for (const item of items) {
  const data = item.json;

  // Track conversions and responses
  if (data.converted) conversions++;
  if (data.responded) responses++;

  // Track offer effectiveness by type
  const offerType = data.offerType || 'unknown';
  if (!offerTypes[offerType]) {
    offerTypes[offerType] = { total: 0, conversions: 0, responses: 0 };
  }
  offerTypes[offerType].total++;
  if (data.converted) offerTypes[offerType].conversions++;
  if (data.responded) offerTypes[offerType].responses++;

  // Identify successful patterns
  if (data.converted) {
    successfulPatterns.push({
      offerType: offerType,
      churnRisk: data.churnRisk || 'unknown',
      customerSegment: data.customerSegment || 'unknown',
      daysSinceLastPurchase: data.daysSinceLastPurchase || 0,
      messageType: data.messageType || 'unknown'
    });
  }
}

// Calculate rates
const conversionRate = totalCampaigns > 0 ? (conversions / totalCampaigns * 100).toFixed(2) : 0;
const responseRate = totalCampaigns > 0 ? (responses / totalCampaigns * 100).toFixed(2) : 0;

// Calculate offer effectiveness
const offerEffectiveness = {};
for (const [type, stats] of Object.entries(offerTypes)) {
  offerEffectiveness[type] = {
    conversionRate: stats.total > 0 ? (stats.conversions / stats.total * 100).toFixed(2) : 0,
    responseRate: stats.total > 0 ? (stats.responses / stats.total * 100).toFixed(2) : 0,
    totalCampaigns: stats.total
  };
}

// Identify patterns in successful campaigns
const patternAnalysis = {};
for (const pattern of successfulPatterns) {
  const key = `${pattern.offerType}_${pattern.churnRisk}_${pattern.customerSegment}`;
  if (!patternAnalysis[key]) {
    patternAnalysis[key] = {
      count: 0,
      offerType: pattern.offerType,
      churnRisk: pattern.churnRisk,
      customerSegment: pattern.customerSegment,
      avgDaysSinceLastPurchase: 0,
      messageTypes: {}
    };
  }
  patternAnalysis[key].count++;
  patternAnalysis[key].avgDaysSinceLastPurchase += pattern.daysSinceLastPurchase;

  const msgType = pattern.messageType;
  patternAnalysis[key].messageTypes[msgType] = (patternAnalysis[key].messageTypes[msgType] || 0) + 1;
}

// Calculate averages and sort patterns
const topPatterns = Object.values(patternAnalysis)
  .map(p => ({
    ...p,
    avgDaysSinceLastPurchase: (p.avgDaysSinceLastPurchase / p.count).toFixed(0)
  }))
  .sort((a, b) => b.count - a.count)
  .slice(0, 5);

// Generate strategy recommendations
const recommendations = [];

// Recommendation based on conversion rate
if (parseFloat(conversionRate) < 5) {
  recommendations.push({
    type: 'conversion_improvement',
    priority: 'high',
    suggestion: 'Conversion rate is below 5%. Consider more aggressive offers or better targeting.'
  });
} else if (parseFloat(conversionRate) > 15) {
  recommendations.push({
    type: 'conversion_success',
    priority: 'low',
    suggestion: 'Excellent conversion rate. Maintain current strategy and scale successful patterns.'
  });
}

// Recommendation based on offer effectiveness
const bestOffer = Object.entries(offerEffectiveness)
  .sort((a, b) => parseFloat(b[1].conversionRate) - parseFloat(a[1].conversionRate))[0];

if (bestOffer) {
  recommendations.push({
    type: 'offer_optimization',
    priority: 'medium',
    suggestion: `${bestOffer[0]} offers show highest conversion rate (${bestOffer[1].conversionRate}%). Increase allocation to this offer type.`
  });
}

// Recommendation based on successful patterns
if (topPatterns.length > 0) {
  const topPattern = topPatterns[0];
  recommendations.push({
    type: 'pattern_replication',
    priority: 'high',
    suggestion: `Most successful pattern: ${topPattern.offerType} for ${topPattern.churnRisk} risk ${topPattern.customerSegment} customers. Replicate this approach.`
  });
}

// Return comprehensive analysis
return [{
  json: {
    summary: {
      totalCampaigns,
      conversions,
      responses,
      conversionRate: parseFloat(conversionRate),
      responseRate: parseFloat(responseRate)
    },
    offerEffectiveness,
    topSuccessfulPatterns: topPatterns,
    recommendations,
    timestamp: new Date().toISOString()
  }
}];

