// Calculate Customer Lifetime Value (CLV) Impact of Retention Campaigns
const items = $input.all();

// Initialize metrics
let totalCustomers = items.length;
let retainedCustomers = 0;
let totalSavedRevenue = 0;
let totalCampaignCost = 0;
let customerMetrics = [];

// Average CLV assumptions (can be customized based on business)
const avgMonthlyRevenue = 100; // Average monthly revenue per customer
const avgCustomerLifetimeMonths = 24; // Average customer lifetime in months
const baselineCLV = avgMonthlyRevenue * avgCustomerLifetimeMonths;

// Process each customer campaign result
for (const item of items) {
  const data = item.json;

  // Determine if customer was retained
  const wasRetained = data.converted || data.responded || false;
  if (wasRetained) retainedCustomers++;

  // Calculate saved revenue for this customer
  const customerCLV = data.historicalCLV || baselineCLV;
  const churnRisk = data.churnRiskScore || 50;
  const churnProbability = churnRisk / 100;

  // Saved revenue = CLV * probability of churn * retention success
  const savedRevenue = wasRetained ? customerCLV * churnProbability : 0;
  totalSavedRevenue += savedRevenue;

  // Estimate campaign cost per customer
  const campaignCost = data.campaignCost || 10; // Default $10 per campaign
  totalCampaignCost += campaignCost;

  // Store individual customer metrics
  customerMetrics.push({
    customerId: data.customerId || data.customerEmail,
    wasRetained,
    churnRisk,
    savedRevenue: savedRevenue.toFixed(2),
    campaignCost: campaignCost.toFixed(2),
    roi: campaignCost > 0 ? ((savedRevenue - campaignCost) / campaignCost * 100).toFixed(2) : 0,
    projectedCLV: wasRetained ? customerCLV : 0
  });
}

// Calculate retention rate improvement
const retentionRate = totalCustomers > 0 ? (retainedCustomers / totalCustomers * 100).toFixed(2) : 0;

// Baseline retention rate (without campaign) - assume 20% would have stayed anyway
const baselineRetentionRate = 20;
const retentionRateImprovement = (parseFloat(retentionRate) - baselineRetentionRate).toFixed(2);

// Calculate campaign ROI
const campaignROI = totalCampaignCost > 0 ? 
  ((totalSavedRevenue - totalCampaignCost) / totalCampaignCost * 100).toFixed(2) : 0;

// Calculate projected long-term value
const projectedLongTermValue = retainedCustomers * baselineCLV;

// Calculate net revenue impact
const netRevenueImpact = totalSavedRevenue - totalCampaignCost;

// Calculate average metrics
const avgSavedRevenuePerCustomer = totalCustomers > 0 ? 
  (totalSavedRevenue / totalCustomers).toFixed(2) : 0;
const avgCampaignCostPerCustomer = totalCustomers > 0 ? 
  (totalCampaignCost / totalCustomers).toFixed(2) : 0;

// Segment analysis by risk level
const highRiskCustomers = customerMetrics.filter(c => c.churnRisk >= 70);
const mediumRiskCustomers = customerMetrics.filter(c => c.churnRisk >= 40 && c.churnRisk < 70);
const lowRiskCustomers = customerMetrics.filter(c => c.churnRisk < 40);

const segmentAnalysis = {
  highRisk: {
    total: highRiskCustomers.length,
    retained: highRiskCustomers.filter(c => c.wasRetained).length,
    retentionRate: highRiskCustomers.length > 0 ? 
      (highRiskCustomers.filter(c => c.wasRetained).length / highRiskCustomers.length * 100).toFixed(2) : 0,
    totalSavedRevenue: highRiskCustomers.reduce((sum, c) => sum + parseFloat(c.savedRevenue), 0).toFixed(2)
  },
  mediumRisk: {
    total: mediumRiskCustomers.length,
    retained: mediumRiskCustomers.filter(c => c.wasRetained).length,
    retentionRate: mediumRiskCustomers.length > 0 ? 
      (mediumRiskCustomers.filter(c => c.wasRetained).length / mediumRiskCustomers.length * 100).toFixed(2) : 0,
    totalSavedRevenue: mediumRiskCustomers.reduce((sum, c) => sum + parseFloat(c.savedRevenue), 0).toFixed(2)
  },
  lowRisk: {
    total: lowRiskCustomers.length,
    retained: lowRiskCustomers.filter(c => c.wasRetained).length,
    retentionRate: lowRiskCustomers.length > 0 ? 
      (lowRiskCustomers.filter(c => c.wasRetained).length / lowRiskCustomers.length * 100).toFixed(2) : 0,
    totalSavedRevenue: lowRiskCustomers.reduce((sum, c) => sum + parseFloat(c.savedRevenue), 0).toFixed(2)
  }
};

// Generate insights and recommendations
const insights = [];

if (parseFloat(campaignROI) > 200) {
  insights.push({
    type: 'success',
    message: `Excellent ROI of ${campaignROI}%. Campaign is highly profitable and should be scaled.`
  });
} else if (parseFloat(campaignROI) > 100) {
  insights.push({
    type: 'success',
    message: `Good ROI of ${campaignROI}%. Campaign is profitable and performing well.`
  });
} else if (parseFloat(campaignROI) > 0) {
  insights.push({
    type: 'warning',
    message: `Moderate ROI of ${campaignROI}%. Consider optimizing campaign costs or targeting.`
  });
} else {
  insights.push({
    type: 'alert',
    message: `Negative ROI of ${campaignROI}%. Campaign needs significant optimization or restructuring.`
  });
}

if (parseFloat(retentionRateImprovement) > 15) {
  insights.push({
    type: 'success',
    message: `Strong retention improvement of ${retentionRateImprovement}% above baseline.`
  });
}

if (parseFloat(segmentAnalysis.highRisk.retentionRate) < 30) {
  insights.push({
    type: 'warning',
    message: `High-risk customer retention is low at ${segmentAnalysis.highRisk.retentionRate}%. Consider more aggressive offers for this segment.`
  });
}

// Return comprehensive CLV impact analysis
return [{
  json: {
    summary: {
      totalCustomers,
      retainedCustomers,
      retentionRate: parseFloat(retentionRate),
      retentionRateImprovement: parseFloat(retentionRateImprovement)
    },
    financial: {
      totalSavedRevenue: totalSavedRevenue.toFixed(2),
      totalCampaignCost: totalCampaignCost.toFixed(2),
      netRevenueImpact: netRevenueImpact.toFixed(2),
      campaignROI: parseFloat(campaignROI),
      avgSavedRevenuePerCustomer: parseFloat(avgSavedRevenuePerCustomer),
      avgCampaignCostPerCustomer: parseFloat(avgCampaignCostPerCustomer)
    },
    longTerm: {
      projectedLongTermValue: projectedLongTermValue.toFixed(2),
      retainedCustomerCount: retainedCustomers,
      avgCLVPerRetainedCustomer: baselineCLV.toFixed(2)
    },
    segmentAnalysis,
    insights,
    timestamp: new Date().toISOString()
  }
}];

