// Calculate dashboard metrics from analytics data
const items = $input.all();

// Initialize metrics
let totalUsers = 0;
let activeUsers = 0;
let totalRevenue = 0;
let churnedUsers = 0;
const userStatuses = {};

// Process each item from analytics data
for (const item of items) {
  const data = item.json;

  // Count total users
  if (data.userId) {
    totalUsers++;

    // Track user status
    const status = data.status || 'unknown';
    userStatuses[status] = (userStatuses[status] || 0) + 1;

    // Count active users
    if (status === 'active') {
      activeUsers++;
    }

    // Count churned users
    if (status === 'churned' || status === 'cancelled') {
      churnedUsers++;
    }

    // Sum revenue
    if (data.revenue) {
      totalRevenue += parseFloat(data.revenue);
    }
  }
}

// Calculate churn rate
const churnRate = totalUsers > 0 ? ((churnedUsers / totalUsers) * 100).toFixed(2) : 0;

// Calculate average revenue per user
const arpu = totalUsers > 0 ? (totalRevenue / totalUsers).toFixed(2) : 0;

// Generate dashboard data
const dashboardData = {
  timestamp: new Date().toISOString(),
  metrics: {
    totalUsers: totalUsers,
    activeUsers: activeUsers,
    churnedUsers: churnedUsers,
    totalRevenue: parseFloat(totalRevenue.toFixed(2)),
    churnRate: parseFloat(churnRate),
    averageRevenuePerUser: parseFloat(arpu)
  },
  userStatusBreakdown: userStatuses,
  summary: {
    activePercentage: totalUsers > 0 ? ((activeUsers / totalUsers) * 100).toFixed(2) : 0,
    revenueFormatted: `$${totalRevenue.toFixed(2)}`,
    health: churnRate < 5 ? 'excellent' : churnRate < 10 ? 'good' : churnRate < 20 ? 'warning' : 'critical'
  }
};

return [{ json: dashboardData }];

