const input = $input.first().json;
const markdown = input.data || input.body || (typeof input === 'string' ? input : JSON.stringify(input));
const today = new Date().toISOString().split('T')[0];

const jobs = [];
const lines = String(markdown).split('\n');

const stopMarkers = ['People also searched', 'About these companies', 'Related to this search', 'Companies and locations', 'Jobs and locations', 'Salaries-', 'Resume Resources', 'Employer Resources'];

let currentJob = null;
let lineAfterTitle = 0;

for (let i = 0; i < lines.length; i++) {
  const line = lines[i].trim();

  if (stopMarkers.some(marker => line.includes(marker))) {
    break;
  }

  const jobMatch = line.match(/^-?\s*##\s*\[(.+?)\]\((https?:\/\/[^)]*viewjob\?jk=([a-zA-Z0-9]+)[^)]*)\)/);

  if (jobMatch) {
    if (currentJob && currentJob.companyName) {
      jobs.push({...currentJob});
    }

    currentJob = {
      jobTitle: jobMatch[1].trim(),
      jobUrl: jobMatch[2].trim(),
      jobId: jobMatch[3],
      companyName: '',
      location: '',
      salary: '',
      jobType: '',
      source: 'Indeed',
      dateFound: today
    };
    lineAfterTitle = 0;
    continue;
  }

  if (currentJob) {
    if (!line || line === '-' || line.startsWith('![') || line.startsWith('- Title:')) continue;
    if (line.includes('Company logo') || line.includes('cloudfront.net')) continue;
    if (line === 'New' || line.includes('Often responds') || line === 'Easily apply' || line.includes('Transit information')) continue;

    lineAfterTitle++;

    if (line.startsWith('$') || line.match(/^\$[\d,]+/)) {
      currentJob.salary = line;
      continue;
    }

    if (line.includes('Pay information not provided')) {
      currentJob.salary = 'Not provided';
      continue;
    }

    if (line.match(/^(Full-time|Part-time|Contract|Temporary|Internship)$/i)) {
      currentJob.jobType = line;
      continue;
    }

    if (line.match(/^(401\(k\)|Health insurance|Paid time off|Vision|Dental|Tuition|Employee discount|Paid parental)/i)) continue;
    if (line.match(/hours per week|Monday to Friday|Day shift|Weekends/i)) continue;

    const locationMatch = line.match(/^(Remote in )?([A-Z][a-zA-Z\s]+),\s*([A-Z]{2})(\s+\d{5})?$/);
    if (locationMatch) {
      currentJob.location = line;
      continue;
    }

    if (line === 'Remote') {
      currentJob.location = 'Remote';
      continue;
    }

    if (!currentJob.companyName && lineAfterTitle <= 5) {
      if (!line.startsWith('[') && !line.startsWith('http') && line.length > 1 && line.length < 100) {
        if (!line.includes('...') && !line.match(/experience|looking for|develop|collaborate|work with/i)) {
          currentJob.companyName = line;
          continue;
        }
      }
    }
  }
}

if (currentJob && currentJob.companyName) {
  jobs.push(currentJob);
}

const validJobs = jobs.filter(job => {
  if (!job.companyName || job.companyName.length < 2) return false;
  if (job.companyName.includes('chevron') || job.companyName.includes('Title:')) return false;
  return true;
});

const seen = new Set();
const uniqueJobs = validJobs.filter(job => {
  const key = job.companyName.toLowerCase().trim();
  if (seen.has(key)) return false;
  seen.add(key);
  return true;
});

if (uniqueJobs.length === 0) {
  return [];
}

return uniqueJobs.map(job => ({ json: job }));

const items = $input.all();

const results = items.map((item, index) => {
  const response = item.json;
  const body = response.body || response;
  const organizations = body.organizations || [];
  const org = organizations[0] || {};

  let originalData = {};
  try {
    originalData = $('Add New Company').item(index).json;
  } catch (e) {
    originalData = {};
  }

  return {
    json: {
      companyName: originalData.companyName || '',
      jobTitle: originalData.jobTitle || '',
      jobUrl: originalData.jobUrl || '',
      location: originalData.location || '',
      dateFound: originalData.dateFound || '',
      linkedinUrl: org.linkedin_url || null,
      organizationId: org.id || null,
      apolloOrganizationName: org.name || null,
      websiteUrl: org.website_url || org.primary_domain || null,
      industry: org.industry || null,
      employeeCount: org.estimated_num_employees || null,
      foundedYear: org.founded_year || null,
      city: org.city || null,
      state: org.state || null,
      country: org.country || null,
      description: org.short_description || null,
      apolloEnriched: !!org.id,
      enrichmentTimestamp: new Date().toISOString()
    }
  };
});

return results;

