const items = $input.all();
const allLeads = [];

items.forEach((item, index) => {
  const response = item.json;
  const body = response.body || response;
  const people = body.people || [];

  if (people.length === 0) return;

  let companyData = {};
  try {
    companyData = $('Extract Apollo Org Data').item(index).json;
  } catch (e) {
    companyData = {};
  }

  people.forEach(person => {
    allLeads.push({
      json: {
        firstName: person.first_name || '',
        lastName: person.last_name || '',
        fullName: person.name || (person.first_name + ' ' + person.last_name).trim(),
        title: person.title || person.headline || '',
        email: person.email || '',
        phone: person.phone_number || '',
        linkedinUrl: person.linkedin_url || '',
        companyName: companyData.companyName || companyData.apolloOrganizationName || '',
        companyWebsite: companyData.websiteUrl || '',
        companyLinkedIn: companyData.linkedinUrl || '',
        country: companyData.country || person.country || '',
        city: person.city || companyData.city || '',
        industry: companyData.industry || '',
        jobTitle: companyData.jobTitle || '',
        jobUrl: companyData.jobUrl || '',
        status: 'New',
        dateAdded: new Date().toISOString().split('T')[0],
        source: 'Indeed + Apollo',
        apolloPersonId: person.id || ''
      }
    });
  });
});

if (allLeads.length === 0) return [];
return allLeads;

