const items = $input.all();

return items.map((item, index) => {
  const openAiResponse = item.json;

  let personalizedMessage = '';
  if (openAiResponse.message && openAiResponse.message.content) {
    personalizedMessage = openAiResponse.message.content;
  } else if (openAiResponse.choices && openAiResponse.choices[0] && openAiResponse.choices[0].message) {
    personalizedMessage = openAiResponse.choices[0].message.content;
  } else if (typeof openAiResponse === 'string') {
    personalizedMessage = openAiResponse;
  }

  let leadData = {};
  try {
    leadData = $('Format Leads').item(index).json;
  } catch (e) {
    leadData = {};
  }

  return {
    json: {
      firstName: leadData.firstName || '',
      lastName: leadData.lastName || '',
      fullName: leadData.fullName || '',
      title: leadData.title || '',
      email: leadData.email || '',
      linkedinUrl: leadData.linkedinUrl || '',
      companyName: leadData.companyName || '',
      country: leadData.country || '',
      city: leadData.city || '',
      industry: leadData.industry || '',
      dateAdded: leadData.dateAdded || '',
      source: leadData.source || '',
      personalizedMessage: personalizedMessage.trim(),
      messageGeneratedAt: new Date().toISOString()
    }
  };
});
