const crypto = require('crypto');

const clientId = $input.first().json.clientId;
const clientSecret = $input.first().json.clientSecret;

const storageKey = crypto.createHash('md5').update(clientId + ':' + clientSecret).digest('hex');

return [{
  json: {
    ...$input.first().json,
    storageKey
  }
}];

const mailingLists = $('Get Mailing List').all();
const targetListName = $('Workflow Configuration').first().json.mailingListWithPhones;
