// Extract and format trending topics from RSS and Reddit
const items = $input.all();

const trends = [];

items.forEach(item => {
  const data = item.json;

  // Handle RSS feed items
  if (data.title && data.link) {
    trends.push({
      source: 'News',
      title: data.title,
      url: data.link || data.url
    });
  }
  // Handle Reddit posts
  else if (data.data?.title) {
    trends.push({
      source: 'Reddit',
      title: data.data.title,
      url: 'https://reddit.com' + data.data.permalink
    });
  }
});

// Create summary
const trendingSummary = trends.map((t, i) => `${i+1}. [${t.source}] ${t.title}`).join('\n');

return [{
  json: {
    trends: trends,
    trendingSummary: trendingSummary,
    totalTrends: trends.length
  }
}];

