// Calculate performance metrics for each campaign
const items = $input.all();

const metrics = items.map(item => {
  const data = item.json;

  // Calculate content quality score (1-10)
  const captionLength = data.postCaption?.length || 0;
  const hashtagCount = (data.hashtags?.match(/#/g) || []).length;
  const hasCreativeDirection = data.creativeDirection?.length > 50;

  let qualityScore = 5; // Base score
  if (captionLength > 100 && captionLength < 300) qualityScore += 2;
  if (hashtagCount >= 5 && hashtagCount <= 10) qualityScore += 2;
  if (hasCreativeDirection) qualityScore += 1;

  return {
    ...data,
    qualityScore,
    captionLength,
    hashtagCount,
    generatedAt: new Date().toISOString()
  };
});

return metrics;
