let postText = $('AI Agent1').first().json.output.post

function cleanPost(text) {
  let cleaned = text.replace(/\\n/g, '\n');
  cleaned = cleaned.replace(/\*\*([^*]+)\*\*/g, '$1');
  cleaned = cleaned.replace(/\*([^*]+)\*/g, '$1');
  cleaned = cleaned.replace(/^#{1,6}\s+/gm, '');
  cleaned = cleaned.replace(/\n{4,}/g, '\n\n\n');
  cleaned = cleaned.trim();

  const hashtagMatch = cleaned.match(/(#\w+(?:\s+#\w+)*)\s*$/);
  if (hashtagMatch) {
    cleaned = cleaned.replace(/(#\w+(?:\s+#\w+)*)\s*$/, '').trim();
    cleaned = cleaned + '\n\n' + hashtagMatch[1];
  }

  return cleaned;
}

try {
  const cleanedPost = cleanPost(postText);

  // Get the binary data from the image generation node
  const imageBinary = $('Generate an image').first().binary.data;

  const output = {
    json: {
      post: cleanedPost,
      _original: postText.substring(0, 500)
    },
    binary: {
      data: imageBinary
    }
  };

  return [output];

} catch (error) {
  return [{
    json: {
      post: postText,
      validation: {
        isValid: false,
        issues: ['Cleaning failed: ' + error.message]
