// Detect Anomalies and Fraud Patterns
// Analyze transaction patterns for suspicious activity

const item = $input.item.json;
const allItems = $input.all();

// Initialize anomaly detection variables
let anomalyScore = 0;
let anomalyFlags = [];
let riskLevel = 'Low';

// Get transaction data
const transactionId = item.transactionId || item.id;
const country = item.country || item.jurisdiction;
const revenue = parseFloat(item.revenue || item.amount || 0);
const transactionDate = new Date(item.transactionDate || item.date || new Date());
const customerType = item.customerType || item.customer_type || 'unknown';

// Calculate statistics from all transactions
const revenues = allItems.map(i => parseFloat(i.json.revenue || i.json.amount || 0));
const avgRevenue = revenues.reduce((a, b) => a + b, 0) / revenues.length;
const maxRevenue = Math.max(...revenues);
const minRevenue = Math.min(...revenues);

// Calculate standard deviation
const variance = revenues.reduce((sum, val) => sum + Math.pow(val - avgRevenue, 2), 0) / revenues.length;
const stdDev = Math.sqrt(variance);

// 1. Check for sudden revenue spikes (outliers)
const zScore = Math.abs((revenue - avgRevenue) / stdDev);
if (zScore > 3) {
  anomalyScore += 30;
  anomalyFlags.push(`Revenue spike detected: ${zScore.toFixed(2)} standard deviations from mean`);
} else if (zScore > 2) {
  anomalyScore += 15;
  anomalyFlags.push(`Unusual revenue amount: ${zScore.toFixed(2)} standard deviations from mean`);
}

// 2. Check for duplicate transaction IDs
const duplicateCount = allItems.filter(i => 
  (i.json.transactionId || i.json.id) === transactionId
).length;

if (duplicateCount > 1) {
  anomalyScore += 40;
  anomalyFlags.push(`Duplicate transaction ID detected: ${duplicateCount} occurrences`);
}

// 3. Check for unusual country combinations
// Flag transactions from high-risk countries or unusual patterns
const highRiskCountries = ['XX', 'YY', 'ZZ']; // Placeholder for actual high-risk countries
if (highRiskCountries.includes(country)) {
  anomalyScore += 20;
  anomalyFlags.push(`Transaction from high-risk country: ${country}`);
}

// Check for unusual country frequency
const countryCount = allItems.filter(i => 
  (i.json.country || i.json.jurisdiction) === country
).length;

const countryFrequency = countryCount / allItems.length;
if (countryFrequency < 0.01 && allItems.length > 100) {
  anomalyScore += 10;
  anomalyFlags.push(`Rare country for transactions: ${country} (${(countryFrequency * 100).toFixed(2)}% of total)`);
}

// 4. Check for transactions outside normal business hours
const hour = transactionDate.getHours();
const dayOfWeek = transactionDate.getDay();

// Flag transactions between 11 PM and 5 AM
if (hour >= 23 || hour < 5) {
  anomalyScore += 15;
  anomalyFlags.push(`Transaction outside business hours: ${hour}:00`);
}

// Flag weekend transactions
if (dayOfWeek === 0 || dayOfWeek === 6) {
  anomalyScore += 10;
  anomalyFlags.push(`Weekend transaction detected`);
}

// 5. Check for suspiciously round numbers
if (revenue > 0 && revenue % 1000 === 0 && revenue >= 10000) {
  anomalyScore += 10;
  anomalyFlags.push(`Suspiciously round revenue amount: $${revenue}`);
}

// 6. Check for rapid successive transactions from same customer
if (customerType !== 'unknown') {
  const customerTransactions = allItems.filter(i => 
    (i.json.customerType || i.json.customer_type) === customerType
  );

  if (customerTransactions.length > 10) {
    anomalyScore += 15;
    anomalyFlags.push(`High transaction frequency for customer type: ${customerType} (${customerTransactions.length} transactions)`);
  }
}

// 7. Check for zero or negative revenue
if (revenue <= 0) {
  anomalyScore += 25;
  anomalyFlags.push(`Invalid revenue amount: $${revenue}`);
}

// 8. Check for extremely high revenue (potential data entry error)
if (revenue > avgRevenue * 100) {
  anomalyScore += 35;
  anomalyFlags.push(`Extremely high revenue: ${(revenue / avgRevenue).toFixed(2)}x average`);
}

// Cap anomaly score at 100
anomalyScore = Math.min(anomalyScore, 100);

// Determine risk level based on anomaly score
if (anomalyScore >= 70) {
  riskLevel = 'High';
} else if (anomalyScore >= 40) {
  riskLevel = 'Medium';
} else {
  riskLevel = 'Low';
}

// Return item with anomaly detection fields
return {
  ...item,
  anomalyScore: anomalyScore,
  anomalyFlags: anomalyFlags,
  riskLevel: riskLevel,
  anomalyDetectionDate: new Date().toISOString(),
  flagCount: anomalyFlags.length
};

// Normalize all transaction amounts to USD using exchange rates

// Get the current transaction item
const item = $input.item.json;

// Get exchange rates from the Fetch Exchange Rates node
const exchangeRatesData = $('Fetch Exchange Rates').first().json;
const rates = exchangeRatesData.rates || {};

// Get transaction currency and revenue
const currency = item.currency || 'USD';
const revenue = parseFloat(item.revenue || item.amount || 0);

// Calculate revenue in USD
let revenueUSD = revenue;

if (currency !== 'USD') {
  const exchangeRate = rates[currency];

  if (exchangeRate && exchangeRate > 0) {
    // Convert to USD by dividing by the exchange rate
    revenueUSD = revenue / exchangeRate;
  } else {
    // If exchange rate not found, log warning and keep original value
    console.log(`Warning: Exchange rate not found for currency ${currency}`);
  }
}

// Return item with added revenueUSD field
return {
  ...item,
  revenueUSD: parseFloat(revenueUSD.toFixed(2))
};

// Generate Predictive Tax Forecast
// Compare current period metrics with historical data to calculate growth trends,
// predict next quarter tax obligations by region, identify jurisdictions approaching
// registration thresholds, and forecast potential compliance risks.

const items = $input.all();

// Get historical trends data (from Merge Historical Trends node)
const historicalData = items.find(item => item.json.historicalTrends) || {};
const currentMetrics = items.find(item => item.json.summary) || {};

// Define registration thresholds by jurisdiction
const registrationThresholds = {
  // EU VAT thresholds (annual)
  'DE': 10000, 'FR': 10000, 'IT': 10000, 'ES': 10000, 'NL': 10000,
  'BE': 10000, 'AT': 10000, 'PL': 10000, 'SE': 10000, 'DK': 10000,
  'FI': 10000, 'IE': 10000, 'PT': 10000, 'GR': 10000, 'CZ': 10000,
  'RO': 10000, 'HU': 10000, 'GB': 10000, 'UK': 10000,

  // US sales tax thresholds (annual)
  'US': 100000, 'US-CA': 500000, 'US-NY': 500000, 'US-TX': 500000,
  'US-FL': 100000, 'US-IL': 100000, 'US-WA': 100000,

  // Other jurisdictions
  'CA': 30000, 'AU': 75000, 'NZ': 60000, 'SG': 1000000,
  'JP': 10000000, 'IN': 2000000, 'BR': 81000
};

// Calculate growth rate from historical data
function calculateGrowthRate(current, historical) {
  if (!historical || historical === 0) return 0;
  return ((current - historical) / historical) * 100;
}

// Predict next quarter based on growth trend
function predictNextQuarter(current, growthRate) {
  const quarterlyGrowth = growthRate / 4; // Convert annual to quarterly
  return current * (1 + (quarterlyGrowth / 100));
}

// Calculate compliance risk score (0-100)
function calculateComplianceRisk(metrics) {
  let riskScore = 0;

  // Factor 1: Number of jurisdictions (more = higher complexity)
  const jurisdictionCount = metrics.jurisdictionCount || 0;
  riskScore += Math.min(jurisdictionCount * 2, 30);

  // Factor 2: Revenue volatility
  const growthRate = Math.abs(metrics.growthRate || 0);
  if (growthRate > 50) riskScore += 25;
  else if (growthRate > 25) riskScore += 15;
  else if (growthRate > 10) riskScore += 5;

  // Factor 3: Approaching thresholds
  const thresholdWarnings = metrics.thresholdWarnings || [];
  riskScore += Math.min(thresholdWarnings.length * 10, 30);

  // Factor 4: Non-compliant jurisdictions
  const nonCompliantCount = metrics.nonCompliantCount || 0;
  riskScore += Math.min(nonCompliantCount * 15, 15);

  return Math.min(riskScore, 100);
}

// Process each region
const forecasts = [];

for (const item of items) {
  const region = item.json.region;
  const summary = item.json.summary || {};

  // Get current period data
  const currentRevenue = summary.totalRevenue || 0;
  const currentTax = summary.totalTax || 0;
  const jurisdictions = summary.jurisdictions || [];
  const jurisdictionCount = summary.jurisdictionCount || 0;

  // Get historical data (simulated - in real scenario would come from database)
  const historicalRevenue = currentRevenue * 0.85; // Assume 15% growth from last period
  const historicalTax = currentTax * 0.85;

  // Calculate growth rate
  const revenueGrowthRate = calculateGrowthRate(currentRevenue, historicalRevenue);
  const taxGrowthRate = calculateGrowthRate(currentTax, historicalTax);

  // Predict next quarter
  const forecastedRevenue = predictNextQuarter(currentRevenue, revenueGrowthRate);
  const forecastedTax = predictNextQuarter(currentTax, taxGrowthRate);

  // Identify threshold warnings
  const thresholdWarnings = [];

  for (const jurisdiction of jurisdictions) {
    const threshold = registrationThresholds[jurisdiction];
    if (threshold) {
      // Estimate jurisdiction revenue (simplified - would need actual data)
      const estimatedJurisdictionRevenue = currentRevenue / jurisdictionCount;
      const forecastedJurisdictionRevenue = forecastedRevenue / jurisdictionCount;

      // Check if approaching threshold (within 20%)
      const thresholdProximity = (forecastedJurisdictionRevenue / threshold) * 100;

      if (thresholdProximity >= 80 && thresholdProximity < 100) {
        thresholdWarnings.push({
          jurisdiction: jurisdiction,
          threshold: threshold,
          currentRevenue: Math.round(estimatedJurisdictionRevenue),
          forecastedRevenue: Math.round(forecastedJurisdictionRevenue),
          proximityPercent: Math.round(thresholdProximity),
          status: 'Approaching Threshold',
          action: 'Monitor closely - may require registration next quarter'
        });
      } else if (thresholdProximity >= 100) {
        thresholdWarnings.push({
          jurisdiction: jurisdiction,
          threshold: threshold,
          currentRevenue: Math.round(estimatedJurisdictionRevenue),
          forecastedRevenue: Math.round(forecastedJurisdictionRevenue),
          proximityPercent: Math.round(thresholdProximity),
          status: 'Threshold Exceeded',
          action: 'Registration required - immediate action needed'
        });
      }
    }
  }

  // Calculate compliance risk score
  const complianceRiskScore = calculateComplianceRisk({
    jurisdictionCount: jurisdictionCount,
    growthRate: revenueGrowthRate,
    thresholdWarnings: thresholdWarnings,
    nonCompliantCount: 0 // Would come from actual compliance data
  });

  // Determine risk level
  let riskLevel = 'Low';
  if (complianceRiskScore >= 70) riskLevel = 'High';
  else if (complianceRiskScore >= 40) riskLevel = 'Medium';

  // Create forecast object
  forecasts.push({
    json: {
      region: region,
      forecastPeriod: 'Q' + (Math.floor(new Date().getMonth() / 3) + 2) + ' ' + new Date().getFullYear(),
      currentPeriod: {
        revenue: Math.round(currentRevenue),
        tax: Math.round(currentTax),
        jurisdictions: jurisdictionCount
      },
      forecast: {
        forecastedRevenue: Math.round(forecastedRevenue),
        forecastedTax: Math.round(forecastedTax),
        growthRate: Math.round(revenueGrowthRate * 10) / 10,
        taxGrowthRate: Math.round(taxGrowthRate * 10) / 10
      },
      thresholdWarnings: thresholdWarnings,
      compliance: {
        complianceRiskScore: Math.round(complianceRiskScore),
        riskLevel: riskLevel,
        recommendedActions: thresholdWarnings.length > 0 
          ? ['Review registration requirements', 'Consult tax advisor', 'Update compliance calendar']
          : ['Continue monitoring', 'Maintain current compliance procedures']
      },
      summary: summary,
      generatedAt: new Date().toISOString()
    }
  });
}

return forecasts;
