const item = $input.first();
const text = item.json.text || '';

const patterns = {
  invoiceNumber: /invoice\s*#?:?\s*([A-Z0-9-]+)/i,
  date: /(\d{1,2}[\/\-]\d{1,2}[\/\-]\d{2,4})/,
  total: /total:?\s*\$?([\d,]+\.?\d*)/i,
  vendor: /from:?\s*([^\n]+)/i,
  dueDate: /due\s*date:?\s*(\d{1,2}[\/\-]\d{1,2}[\/\-]\d{2,4})/i
};

const extracted = {
  rawText: text.substring(0, 2000),
  invoiceNumber: text.match(patterns.invoiceNumber)?.[1] || 'UNKNOWN',
  invoiceDate: text.match(patterns.date)?.[1] || new Date().toISOString().split('T')[0],
  totalAmount: parseFloat((text.match(patterns.total)?.[1] || '0').replace(',', '')) || 0,
  vendorName: text.match(patterns.vendor)?.[1]?.trim() || 'Unknown Vendor',
  dueDate: text.match(patterns.dueDate)?.[1] || null,
  fileName: item.json.fileName || 'invoice.pdf',
  processedAt: new Date().toISOString()
};

return [{ json: extracted }];

