parameters.jsCode:
const item = $input.first();
const originalData = $('Parse Invoice Data').first().json;

let classification;
try {
  const responseText = item.json.output || item.json.text || '';
  const jsonMatch = responseText.match(/\{[\s\S]*\}/);
  classification = jsonMatch ? JSON.parse(jsonMatch[0]) : {
    category: 'Other',
    glCode: '6900',
    confidence: 0.5,
    requiresApproval: true,
    anomalyDetected: false,
    anomalyReason: null,
    summary: 'Classification failed - manual review required'
  };
} catch (e) {
  classification = {
    category: 'Other',
    glCode: '6900',
    confidence: 0.5,
    requiresApproval: true,
    anomalyDetected: false,
    anomalyReason: null,
    summary: 'Parse error: ' + e.message
  };
}

return [{
  json: {
    ...originalData,
    classification: classification,
    needsApproval: classification.requiresApproval || originalData.totalAmount > 5000,
    processingStatus: 'classified'
  }
}];

