// Get Next Business Day and produce timestamps for the 9:00-18:00 window
let date = new Date();
// Move to tomorrow
date.setDate(date.getDate() + 1);

// Weekend skip logic (0=Sun,6=Sat)
const day = date.getDay();
if (day === 6) {
 date.setDate(date.getDate() + 2); // Sat -> Mon
} else if (day === 0) {
 date.setDate(date.getDate() + 1); // Sun -> Mon
}

// Format helper
function pad(n) {
 return n < 10 ? '0' + n : n;
}

const yyyy = date.getFullYear();
const mm = pad(date.getMonth() + 1);
const dd = pad(date.getDate());

// Build timestamps EXACTLY like "2025-12-10 09:00:00"
const windowStart = ${yyyy}-${mm}-${dd} 09:00:00;
const windowEnd = ${yyyy}-${mm}-${dd} 18:00:00;

const defaultSlotStart = ${yyyy}-${mm}-${dd} 09:00:00;
const defaultSlotEnd = ${yyyy}-${mm}-${dd} 10:00:00;

return [
 {
 json: {
 nextBusinessDay: ${yyyy}-${mm}-${dd},
 windowStart,
 windowEnd,
 defaultSlotStart,
 defaultSlotEnd
 }
 }
];
