// Get the first item JSON
const item = $('Loop Over Items').first().json;

// Create dynamic HTML content
const htmlContent = `
<!DOCTYPE html>
<html>
<head>
  <meta charset="UTF-8">
  <title>Shipment Report</title>
  <style>
    body {
      font-family: 'Segoe UI', Arial, sans-serif;
      margin: 30px;
      color: #222;
      background-color: #f4f7fb;
    }

    .header {
      background: linear-gradient(135deg, #004aad, #007bff);
      color: white;
      padding: 25px;
      text-align: center;
      border-radius: 12px;
      margin-bottom: 30px;
      box-shadow: 0 4px 10px rgba(0,0,0,0.15);
    }

    .header h1 {
      margin: 0;
      font-size: 32px;
      letter-spacing: 1px;
    }

    .section {
      margin-bottom: 25px;
      padding: 20px;
      border: none;
      border-radius: 12px;
      background-color: white;
      box-shadow: 0 4px 8px rgba(0,0,0,0.1);
    }

    .section h2 {
      margin-top: 0;
      color: #004aad;
      font-size: 22px;
      border-bottom: 2px solid #004aad;
      padding-bottom: 8px;
    }

    table {
      width: 100%;
      border-collapse: separate;
      border-spacing: 0;
      margin-top: 15px;
      font-size: 16px;
    }

    th, td {
      border: 1px solid #ddd;
      padding: 14px;
      text-align: left;
    }

    th {
      background-color: #004aad;
      color: white;
      font-weight: bold;
      font-size: 16px;
    }

    tr:nth-child(even) {
      background-color: #f9faff;
    }

    tr:hover {
      background-color: #eef5ff;
    }

    .footer {
      margin-top: 40px;
      text-align: center;
      font-size: 14px;
      color: #555;
      padding: 15px;
      border-top: 2px solid #ddd;
    }
  </style>
</head>
<body>
  <div class="header">
    <h1>üì¶ Shipment Report</h1>
  </div>

  <div class="section">
    <h2>Shipment Details</h2>
    <p><strong>Shipment ID:</strong> ${item['Shipment ID / Reference Number']}</p>
    <p><strong>Shipper:</strong> ${item['Shipper / Sender Name & Address']}</p>
    <p><strong>Receiver:</strong> ${item['Receiver / Consignee Name & Address']}</p>
  </div>

  <div class="section">
    <h2>Goods Information</h2>
    <table>
      <tr>
        <th>Item</th>
        <th>Quantity</th>
        <th>Weight</th>
        <th>HS Code</th>
      </tr>
      <tr>
        <td>${item['Description of Goods']}</td>
        <td>${item['Quantity / Units']}</td>
        <td>${item['Weight / Volume']}</td>
        <td>${item['HS Codes']}</td>
      </tr>
    </table>
  </div>

  <div class="footer">
    üö¢ Generated on <strong>${item['Carrier Name']}</strong> | Logistics System
  </div>
</body>
</html>
`;

// Convert HTML to binary format for n8n
const buffer = Buffer.from(htmlContent, 'utf8');
const binaryData = {
  data: buffer.toString('base64'),
  mimeType: 'text/html',
  fileName: 'Shipment_Report.html',
};

// Attach binary data to the first item
items[0].binary = { data: binaryData };

return items;

