];

// Flatten the "listings" array so that each property becomes its own item.
const output = [];
const now = new Date().toISOString();
for (const item of $input.all()) {
  const listings = item.json.listings || [];
  for (const listing of listings) {
    output.push({ json: { ...listing, scrapedAt: now } });
  }
}
return output;

/* Basic validation & enrichment */
try {
  const item = $input.first().json;
  if (!item.address || !item.price || !item.size_sqft) {
    throw new Error('Missing critical fields');
  }
  const cleanPrice = parseFloat(String(item.price).replace(/[^0-9.]/g, ''));
  const cleanSize = parseFloat(String(item.size_sqft).replace(/[^0-9.]/g, ''));
  item.price = cleanPrice;
  item.size_sqft = cleanSize;
  item.pricePerSqft = cleanPrice && cleanSize ? +(cleanPrice / cleanSize).toFixed(2) : null;
  item.validated = true;
  return [{ json: item }];
} catch (e) {
  return [{ json: { error: true, message: e.message } }];
}

