// Calculate AI Search Gap Analysis
const config = $('Configuration').first().json;
const yourData = [
  $('Your Domain - ChatGPT').first().json,
  $('Your Domain - Perplexity').first().json,
  $('Your Domain - Gemini').first().json
];
const compData = [
  $('Competitor - ChatGPT').first().json,
  $('Competitor - Perplexity').first().json,
  $('Competitor - Gemini').first().json
];

const engines = ['chatgpt', 'perplexity', 'gemini'];
const results = [];

engines.forEach((engine, i) => {
  const your = yourData[i]?.summary || {};
  const comp = compData[i]?.summary || {};

  const yourLinkPresence = your.link_presence?.current || 0;
  const compLinkPresence = comp.link_presence?.current || 0;
  const yourAvgPos = your.average_position?.current || 0;
  const compAvgPos = comp.average_position?.current || 0;
  const yourTraffic = your.ai_opportunity_traffic?.current || 0;
  const compTraffic = comp.ai_opportunity_traffic?.current || 0;

  // Calculate gaps (negative = competitor is better)
  const linkPresenceGap = yourLinkPresence - compLinkPresence;
  const avgPosGap = compAvgPos - yourAvgPos; // Lower is better, so flip
  const trafficGap = yourTraffic - compTraffic;

  // Opportunity Score (higher = bigger opportunity to improve)
  // Formula: Weighted average of gaps (negative gaps = opportunities)
  const opportunityScore = Math.round(
    (Math.abs(Math.min(linkPresenceGap, 0)) * 0.4) +
    (Math.abs(Math.min(avgPosGap, 0)) * 0.3) +
    (Math.abs(Math.min(trafficGap, 0)) * 0.3)
  );

  results.push({
    json: {
      analysis_date: new Date().toISOString().split('T')[0],
      your_domain: config.your_domain,
      competitor_domain: config.competitor_domain,
      engine: engine,

      // Your metrics
      your_link_presence: yourLinkPresence,
      your_avg_position: yourAvgPos.toFixed(2),
      your_ai_traffic: yourTraffic,

      // Competitor metrics
      comp_link_presence: compLinkPresence,
      comp_avg_position: compAvgPos.toFixed(2),
      comp_ai_traffic: compTraffic,

      // Gap analysis
      link_presence_gap: linkPresenceGap,
      avg_position_gap: avgPosGap.toFixed(2),
      traffic_gap: trafficGap,

      // Opportunity scoring
      opportunity_score: opportunityScore,
      priority: opportunityScore > 100 ? 'HIGH' : opportunityScore > 50 ? 'MEDIUM' : 'LOW',

      // Status flags
      is_competitor_winning: linkPresenceGap < 0 || avgPosGap < 0,
      needs_immediate_action: opportunityScore > 100
    }
  });
});

return results;

// Extract top competitor prompts and format for keyword research
const input = $input.first().json;
const prompts = input.prompts || [];

// Sort by volume (even if 0) and take top prompts
// Filter can be adjusted based on actual data characteristics
const topPrompts = prompts
  .filter(p => p.prompt && p.prompt.trim().length > 0) // Just check prompt exists
  .slice(0, 20) // Take top 20
  .map(p => p.prompt);

return [{
  json: {
    keywords: topPrompts,
    count: topPrompts.length,
    original_total: input.total || 0
  }
}];

// Extract competitor keywords that you don't rank for
const keywords = $input.all();
const topKeywords = keywords
  .map(k => k.json)
  .filter(k => k.volume > 500 && k.position <= 10) // High-value, top-ranking
  .slice(0, 30)
  .map(k => k.keyword);

return [{
  json: {
    keywords: topKeywords,
    count: topKeywords.length
  }
}];

// Final Opportunity Scoring & Prioritization
const aiGaps = $('Calculate AI Gaps').all();
const keywordMetrics = $('Gap Keywords - Full Metrics').all();
const backlinkData = $('Competitor Backlink Authority').first().json;
const config = $('Configuration').first().json;

// Competitor authority context
const compAuthority = {
  total_backlinks: backlinkData.backlinks_info?.backlinks || 0,
  referring_domains: backlinkData.backlinks_info?.refdomains || 0,
  domain_inlink_rank: backlinkData.backlinks_info?.domain_inlink_rank || 0
};

const opportunities = [];

// Process AI gaps by engine
aiGaps.forEach(gap => {
  const g = gap.json;
  if (g.is_competitor_winning) {
    opportunities.push({
      type: 'AI_VISIBILITY_GAP',
      engine: g.engine,
      priority: g.priority,
      opportunity_score: g.opportunity_score,
      metric: 'Link Presence',
      your_value: g.your_link_presence,
      comp_value: g.comp_link_presence,
      gap: g.link_presence_gap,
      action: g.link_presence_gap < -50 ? 'URGENT: Create AI-optimized content' : 'Improve content for AI citations'
    });
  }
});

// Process keyword gaps
keywordMetrics.forEach(kw => {
  const k = kw.json;
  if (k.volume > 1000 && k.difficulty < 70) {
    // Calculate opportunity score
    const opportunityScore = Math.round(
      (k.volume * 0.5) + 
      ((100 - k.difficulty) * 0.3) +
      (k.cpc * 10)
    );

    opportunities.push({
      type: 'KEYWORD_GAP',
      keyword: k.keyword,
      priority: opportunityScore > 5000 ? 'HIGH' : opportunityScore > 2000 ? 'MEDIUM' : 'LOW',
      opportunity_score: opportunityScore,
      volume: k.volume,
      difficulty: k.difficulty,
      cpc: k.cpc,
      estimated_traffic: Math.round(k.volume * 0.15), // Assume 15% CTR at top position
      action: k.difficulty < 40 ? 'Quick Win: Create optimized content' : 'Long-term: Build authority'
    });
  }
});

// Sort all opportunities by score
opportunities.sort((a, b) => b.opportunity_score - a.opportunity_score);

// Add metadata
const finalReport = opportunities.slice(0, 50).map((opp, i) => ({
  json: {
    rank: i + 1,
    analysis_date: new Date().toISOString().split('T')[0],
    your_domain: config.your_domain,
    competitor_domain: config.competitor_domain,
    competitor_authority: compAuthority.domain_inlink_rank,
    competitor_backlinks: compAuthority.total_backlinks,
    competitor_ref_domains: compAuthority.referring_domains,
    ...opp
  }
}));

return finalReport;

// Combine prompts and keywords into a single unified list
const allInputs = $input.all();

// Extract all keyword arrays
const allKeywords = [];

allInputs.forEach(item => {
  if (item.json.keywords && Array.isArray(item.json.keywords)) {
    allKeywords.push(...item.json.keywords);
  }
});

// Remove duplicates (case-insensitive) and clean
const uniqueKeywords = [...new Set(
  allKeywords.map(kw => kw.toLowerCase().trim())
)];

// Filter out inappropriate/adult content keywords
const inappropriateTerms = [
  'porn', 'xxx', 'sex', 'adult', 'tube', 'leaked', 'incest', 
  'coomer', 'ashemaletube', 'missav', 'xpaja', 'javmost', 
  'sickjunk', 'xfreehd', 'celebjihad', 'ghettotube', 'bbw', 
  'boundhub', 'potnhub', 'pormhub', 'xorgasmo', 'ukdevilz',
  'ero', 'thots', 'anonib', 'scrolller', 'webtoon', 'whoer',
  'enf', 'cmnf', 'shahvani', 'leakedzone', 'sickjunk'
];

const cleanKeywords = uniqueKeywords.filter(kw => {
  return !inappropriateTerms.some(term => kw.includes(term));
});

// Additional validation - ensure keywords are valid strings
const validKeywords = cleanKeywords.filter(kw => {
  return kw && 
         kw.length > 0 && 
         kw.length < 200 && // SE Ranking limit
         /^[a-zA-Z0-9\s\-\?\.]+$/.test(kw); // Only alphanumeric, spaces, hyphens, question marks, periods
});

return [{
  json: {
    keywords: validKeywords.slice(0, 50), // Limit to 50 for API efficiency
    total_count: validKeywords.length,
    filtered_out: uniqueKeywords.length - validKeywords.length,
    prompts_count: allInputs[0]?.json?.count || 0,
    competitor_keywords_count: allInputs[1]?.json?.count || 0
  }
}];
