// Get all items from the input
const items = $input.all();

// Get all website domains from the sheet
const sheetData = $('Get Company List from Sheet').all();

// Process each item and match it with the corresponding website domain by index
const results = items.map((item, index) => {
  const text = item.json.main_content;

  // Split into lines and filter out noise
  const lines = text.split('\n').filter(line => {
    const trimmed = line.trim();

    // Skip empty lines
    if (!trimmed) return false;

    // Skip common noise patterns
    if (trimmed.match(/^(Sign In|Book a Demo|Continue|Start for Free|Get a|Learn More)$/i)) return false;
    if (trimmed.includes('javascript:void')) return false;
    if (trimmed.includes('Thank you! Your submission')) return false;
    if (trimmed.includes('Oops! Something went wrong')) return false;
    if (trimmed.match(/^\[.*?\]$/)) return false; // Remove [links]
    if (trimmed.match(/^(Â©|Terms and conditions|Privacy Policy)/)) return false;
    if (trimmed.match(/^(LinkedIn|Twitter|Facebook)$/)) return false;
    if (trimmed.length < 10) return false; // Skip very short lines

    return true;
  });

  // Join back and clean up excessive whitespace
  const cleaned = lines.join('\n').replace(/\n{3,}/g, '\n\n').trim();

  return { 
    json: { 
      main_content: cleaned,
      website_domain: sheetData[index].json['Website Domain']
    } 
  };
});

return results;
