// Get the form submission data
const formData = $('Event Registration Form3').first().json;
const submittedEmail = formData.Email.toLowerCase().trim();

// Get all existing registrations from the sheet
const existingRegistrations = $('Read Existing Registrations2').all();

// Check if email already exists
const isDuplicate = existingRegistrations.some(item => {
  const existingEmail = item.json.Email;
  if (existingEmail) {
    return existingEmail.toLowerCase().trim() === submittedEmail;
  }
  return false;
});

// Return the form data with duplicate status
return [{
  json: {
    ...formData,
    isDuplicate: isDuplicate,
    duplicateCheckAt: new Date().toISOString()
  }
}];

const registrationData = $('Add Status & Event Date3').first().json;

// Get the email send result from Gmail (contains id, threadId, labelIds)
const emailResult = $input.first().json;

// Check if email was sent successfully (has an id)
const emailSentSuccessfully = emailResult.id ? true : false;

// Return the original registration data so it can be used 
// in the "Update Welcome Email Status" node
return [{
  json: {
    // All original registration fields
    'First Name': registrationData['First Name'],
    'Last Name': registrationData['Last Name'],
    'Email': registrationData['Email'],
    'Company': registrationData['Company'],
    'Interests': registrationData['Interests'],
    'Status': registrationData['Status'],
    'preEventEmailSent ': registrationData['preEventEmailSent '], // Note: includes trailing space
    'Event Date': registrationData['Event Date'],

    // Add confirmation that email was sent
    'welcomeEmailSent': 'True',
    'emailSentAt': new Date().toISOString(),
    'emailId': emailResult.id,
    'emailThreadId': emailResult.threadId
  }
}];

const items = $input.all();
const today = new Date();
today.setHours(0, 0, 0, 0);

const reminderCandidates = items.filter(item => {
  const data = item.json;

  // Only process confirmed registrations
  if (data.Status !== 'confirmed') {
    return false;
  }

  // Parse the event date (format: DD-MM-YYYY)
  const eventDateParts = data['Event Date'].split('-');
  const eventDate = new Date(
    parseInt(eventDateParts[2]), // year
    parseInt(eventDateParts[1]) - 1, // month (0-indexed)
    parseInt(eventDateParts[0]) // day
  );
  eventDate.setHours(0, 0, 0, 0);

  // Calculate days until event
  const diffTime = eventDate - today;
  const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

  // Check for 3-day reminder or event day reminder
  const is3DayReminder = (diffDays === 3 && data['preEventEmailSent '] === false);
  const isEventDayReminder = (diffDays === 0 && (!data['eventDayEmailSent '] || data['eventDayEmailSent '] === false));

  return is3DayReminder || isEventDayReminder;
});

// Add reminder type to each item
const processedItems = reminderCandidates.map(item => {
  const data = item.json;
  const eventDateParts = data['Event Date'].split('-');
  const eventDate = new Date(
    parseInt(eventDateParts[2]),
    parseInt(eventDateParts[1]) - 1,
    parseInt(eventDateParts[0])
  );
  eventDate.setHours(0, 0, 0, 0);

  const diffTime = eventDate - today;
  const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

  return {
    json: {
      ...data,
      reminderType: diffDays === 3 ? '3-day' : 'event-day',
      daysUntilEvent: diffDays
    }
  };
});

return processedItems;

