// $items('Config') ‚Üí returns all items coming from the node named Config
//[0] ‚Üí takes the first item in that array
// .json ‚Üí accesses the JSON payload of that item
//.DAYS_BACK ‚Üí reads this field from the JSON
// || 7 is a fallback. If DAYS_BACK is missing, null or undefined

const daysBack = $items('Config')[0].json.DAYS_BACK || 7;

// Calculate the cutoff based on date Now and DAYS_BACK in ms
const cutoff = new Date(Date.now() - daysBack*24*60*60*1000);

// // Get all incoming items and filter them
// If i.json.lastmod exists ‚Üí create a Date from it otherwise ‚Üí set the value to null
const items = $items().filter(i => {
  const lm = i.json.lastmod ? new Date(i.json.lastmod) : null;

  // Filter lm > cutoff
  // After filtering, keep only the fields we care about

  return lm && lm >= cutoff;}).map(i => ({ json: { loc: i.json.loc, lastmod: i.json.lastmod } }));

// Return the final list of items to the next node

return items;

const batch = $items();
if (!batch.length) return [];

// 1) Pull & sanitize raw URLs 

let urls = batch
  .map(i => (i && i.json && i.json.loc) ? String(i.json.loc).trim() : null)
  .filter(u => !!u); 

// 2) Keep only absolute http/https
urls = urls.filter(u => /^https?:\/\//i.test(u));

// 3) Ensure they match your site host (precompute host once)
const siteUrl = $items('Config')[0].json.SITE_URL || '';
const m = siteUrl.match(/^https?:\/\/([^/]+)/i);
const host = m ? m[1] : '';

// If you prefer to pass HOST explicitly in Config, replace host above with:
// const host = $items('Config')[0].json.HOST;

urls = urls.filter(u => {
  const h = (u.match(/^https?:\/\/([^/]+)/i) || [])[1] || '';
  return h.toLowerCase() === host.toLowerCase();
});

// 4) De-dupe
urls = Array.from(new Set(urls));

// 5) Guard: IndexNow expects at least 1 valid URL
if (!urls.length || !host) return [];

return [{
  json: {
    host,
    key: $items('Config')[0].json.INDEXNOW_KEY,
    keyLocation: $items('Config')[0].json.INDEXNOW_KEY_URL,
    urlList: urls
  }
}];

/**‚ÄúIf the Config node says USE_INDEXNOW is true, keep all items.
If not, output nothing.‚Äù **/

const cfg = $items('Config')[0]?.json ?? {};
const on = (cfg.USE_INDEXNOW === true) || String(cfg.USE_INDEXNOW).toLowerCase() === 'true';
return on ? $items() : [];

// Gate: Google
const cfg = $items('Config')[0]?.json ?? {};
const on = (cfg.USE_GOOGLE === true) || String(cfg.USE_GOOGLE).toLowerCase() === 'true';
return on ? $items() : [];
